INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Fault in Our Stars', 5, 0, 337, 0, 0, 'Penguin', 'Now a Major Motion Picture TODAY Book Club pick TIME magazine’s #1 Fiction Book of 2012 "The greatest romance story of this decade." —Entertainment Weekly -Millions of copies sold- #1 New York Times Bestseller #1 Wall Street Journal Bestseller #1 USA Today Bestseller #1 International Bestseller #1 Indie Bestseller Despite the tumor-shrinking medical miracle that has bought her a few years, Hazel has never been anything but terminal, her final chapter inscribed upon diagnosis. But when a gorgeous plot twist named Augustus Waters suddenly appears at Cancer Kid Support Group, Hazel’s story is about to be completely rewritten. Insightful, bold, irreverent, and raw, The Fault in Our Stars is award-winning-author John Green’s most ambitious and heartbreaking work yet, brilliantly exploring the funny, thrilling, and tragic business of being alive and in love.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1660273739i/11870085.jpg', '9780525478810', '[''Young Adult'', ''Fiction'', ''Contemporary'', ''Realistic Fiction'', ''Book Club'', ''Coming Of Age'', ''Teen'']', 'en', '2012-01-10');
