INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Angels & Demons', 8, 0, 738, 0, 0, 'Pocket Books', 'The explosive Robert Langdon thriller from Dan Brown, the #1 New York Times bestselling author of The Da Vinci Code and Inferno—now a major film directed by Ron Howard and starring Tom Hanks and Felicity Jones. Angels & Demons careens from enlightening epiphanies to dark truths as the battle between science and religion turns to war. This is the book that started it all: we meet Robert Langdon for the first time, caught up in a race against time to find an apocalyptic time-bomb, planted by an ancient secret society that has surfaced to carry out its ultimate threat: to destroy the Vatican.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1696691404i/960.jpg', '9781416524790', '[''Fiction'', ''Mystery'', ''Thriller'', ''Mystery Thriller'', ''Suspense'', ''Adventure'', ''Crime'']', 'en', '2000-05-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Moby-Dick', 9, 0, 0, 0, 0, 'Modern Library', 'Introduction by Elizabeth Hardwick Illustrations by Rockwell Kent Nominated as one of America’s best-loved novels by PBS’s The Great American Read First published in 1851, Herman Melville’s masterpiece is, in Elizabeth Hardwick’s words, “the greatest novel in American literature.” The saga of Captain Ahab and his monomaniacal pursuit of the white whale remains a peerless adventure story but one full of mythic grandeur, poetic majesty, and symbolic power. Filtered through the consciousness of the novel’s narrator, Ishmael, Moby-Dick draws us into a universe full of fascinating characters and stories, from the noble cannibal Queequeg to the natural history of whales, while reaching existential depths that excite debate and contemplation to this day.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1320399351i/1885.jpg', '9780679783270', '[''Fiction'', ''Historical Fiction'', ''Historical'', ''Literature'', ''Audiobook'', ''Novels'', ''Historical Romance'']', 'en', '1813-01-28');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Kite Runner', 10, 0, 404, 0, 0, 'Penguin', 'The unforgettable, heartbreaking story of the unlikely friendship between a wealthy boy and the son of his father''s servant, The Kite Runner is a beautifully crafted novel set in a country that is in the process of being destroyed. It is about the power of reading, the price of betrayal, and the possibility of redemption, and it is also about the power of fathers over sons—their love, their sacrifices, their lies. The first Afghan novel to be written in English, The Kite Runner tells a sweeping story of family, love, and friendship against a backdrop of history that has not been told in fiction before, bringing to mind the large canvases of the Russian writers of the nineteenth century. But just as it is old-fashioned in its narration, it is contemporary in its subject-the devastating history of Afghanistan over the last thirty years. As emotionally gripping as it is tender, The Kite Runner is an unusual and powerful debut.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1579036753i/77203.jpg', '9781594480000', '[''Fiction'', ''Historical Fiction'', ''Contemporary'', ''Book Club'', ''Novels'', ''Historical'', ''School'']', 'en', '2003-05-29');
