INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Mockingjay', 0, 0, 390, 0, 0, 'Scholastic Press', 'Against all odds, Katniss Everdeen has survived the Hunger Games twice. But now that she''s made it out of the bloody arena alive, she''s still not safe. The Capitol is angry. The Capitol wants revenge. Who do they think should pay for the unrest? Katniss. And what''s worse, President Snow has made it clear that no one else is safe either. Not Katniss''s family, not her friends, not the people of District 12. Powerful and haunting, this thrilling final installment of Suzanne Collins''s groundbreaking The Hunger Games trilogy promises to be one of the most talked about books of the year.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1586722918i/7260188.jpg', '9780439023510', '[''Young Adult'', ''Dystopia'', ''Fiction'', ''Fantasy'', ''Science Fiction'', ''Romance'', ''Adventure'']', 'en', '2010-08-24');
