INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Da Vinci Code', 8, 0, 481, 0, 0, 'Vintage', '#1 WORLDWIDE BESTSELLER • While in Paris, Harvard symbologist Robert Langdon is awakened by a phone call in the dead of the night. The elderly curator of the Louvre has been murdered inside the museum, his body covered in baffling symbols. “Blockbuster perfection.... A gleefully erudite suspense novel.” —The New York Times “A pulse-quickening, brain-teasing adventure.” —People As Langdon and gifted French cryptologist Sophie Neveu sort through the bizarre riddles, they are stunned to discover a trail of clues hidden in the works of Leonardo da Vinci—clues visible for all to see and yet ingeniously disguised by the painter. Even more startling, the late curator was involved in the Priory of Sion—a secret society whose members included Sir Isaac Newton, Victor Hugo, and Da Vinci—and he guarded a breathtaking historical secret. Unless Langdon and Neveu can decipher the labyrinthine puzzle—while avoiding the faceless adversary who shadows their every move—the explosive, ancient truth will be lost forever.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1720313229i/968.jpg', '9780307277670', '[''Fiction'', ''Mystery'', ''Thriller'', ''Mystery Thriller'', ''Historical Fiction'', ''Suspense'', ''Adventure'']', 'en', '2003-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Lord of the Flies', 17, 0, 0, 0, 0, 'National Geographic Books', 'Lord of the Flies remains as provocative today as when it was first published in 1954, igniting passionate debate with its startling, brutal portrait of human nature. Though critically acclaimed, it was largely ignored upon its initial publication. Yet soon it became a cult favorite among both students and literary critics who compared it to J.D. Salinger''s The Catcher in the Rye in its influence on modern thought and literature. William Golding''s compelling story about a group of very ordinary small boys marooned on a coral island has become a modern classic. At first it seems as though it is all going to be great fun; but the fun before long becomes furious and life on the island turns into a nightmare of panic and death. As ordinary standards of behaviour collapse, the whole world the boys know collapses with them—the world of cricket and homework and adventure stories—and another world is revealed beneath, primitive and terrible. Labeled a parable, an allegory, a myth, a morality tale, a parody, a political treatise, even a vision of the apocalypse, Lord of the Flies has established itself as a true classic. "Lord of the Flies is one of my favorite books. That was a big influence on me as a teenager, I still read it every couple of years." —Suzanne Collins, author of The Hunger Games "As exciting, relevant, and thought-provoking now as it was when Golding published it in 1954." —Stephen King', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327869409i/7624.jpg', '9780140283330', '[''Fiction'', ''Classics'', ''Young Adult'', ''School'', ''Dystopia'', ''Literature'', ''Read For School'']', 'en', '1954-09-17');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Hamlet', 11, 0, 432, 0, 0, 'Simon and Schuster', 'Folger''s Shakespeare Library presents these definitive editions of Shakespeare''s classic tragedies, featuring scene-by-scene plot summaries, full explanatory notes, and much more. Original. (Plays/Drama)', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1629680008i/18135.jpg', '9780743477120', '[''Classics'', ''Plays'', ''Fiction'', ''School'', ''Read For School'', ''Literature'', ''High School'']', 'en', '1597-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Help', 18, 0, 0, 0, 0, 'National Geographic Books', 'The #1 New York Times bestselling novel and basis for the Academy Award-winning film—a timeless and universal story about the lines we abide by, and the ones we don’t—nominated as one of America’s best-loved novels by PBS’s The Great American Read. Aibileen is a black maid in 1962 Jackson, Mississippi, who’s always taken orders quietly, but lately she’s unable to hold her bitterness back. Her friend Minny has never held her tongue but now must somehow keep secrets about her employer that leave her speechless. White socialite Skeeter just graduated college. She’s full of ambition, but without a husband, she’s considered a failure. Together, these seemingly different women join together to write a tell-all book about work as a black maid in the South, that could forever alter their destinies and the life of a small town...', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1622355533i/4667024.jpg', '9780399155340', '[''Fiction'', ''Historical Fiction'', ''Book Club'', ''Historical'', ''Adult'', ''Adult Fiction'', ''Contemporary'']', 'en', '2009-02-10');
