INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Gweilo', 30, 0, 386, 0, 0, 'Random House', 'Shadowed by the unhappiness of his warring parents, a broad-minded mother and a bigoted father, Martin Booth''s memoir of his childhood in Hong Kong in the early 1950s is a journey into Chinese culture and an extinct colonial way of life.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1385738917i/15931.jpg', '9780553816720', '[''Romance'', ''Fiction'', ''Chick Lit'', ''Contemporary'', ''Adult'', ''Classics'', ''Adult Fiction'']', 'en', '1996-10-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Water for Elephants', 31, 0, 370, 0, 0, 'Algonquin Books', 'Ninety-something-year-old Jacob Jankowski remembers his time in the circus as a young man during the Great Depression, and his friendship with Marlena, the star of the equestrian act, and Rosie, the elephant, who gave them hope. Reader''s Guide included. Reprint.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1667708346i/43641.jpg', '9781565125600', '[''Fiction'', ''Historical Fiction'', ''Romance'', ''Book Club'', ''Historical'', ''Adult'', ''Adult Fiction'']', 'en', '2006-05-22');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Book Thief', 32, 0, 582, 0, 0, 'Knopf Books for Young Readers', 'Trying to make sense of the horrors of World War II, Death is fascinated by one young girl, Liesel, whose book-stealing and story-telling talents help sustain her community, especially her family and the Jewish man they are hiding.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1522157426i/19063.jpg', '9780375831000', '[''Historical Fiction'', ''Fiction'', ''Young Adult'', ''Classics'', ''Historical'', ''Book Club'', ''War'']', 'en', '2005-09-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Where the Sidewalk Ends', 33, 0, 166, 0, 0, 'Harpercollins Childrens Books', 'A boy who turns into a TV set and a girl who eats a whale are only two of the characters in a collection of humorous poetry illustrated with the author''s own drawings.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1168052448i/30119.jpg', '9780060513030', '[''Poetry'', ''Childrens'', ''Classics'', ''Fiction'', ''Humor'', ''Young Adult'', ''Picture Books'']', 'en', '1974-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('City of Bones', 34, 0, 504, 0, 0, 'Atria Paperback', 'City of fallen angels: As mysterious murders threaten the new peace between Shadowhunters and Downworlders, only Simon, the Daylighter vampire, can help bring both groups together.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1432730315i/256683.jpg', '9781416914280', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Paranormal'', ''Urban Fantasy'', ''Fiction'', ''Vampires'']', 'en', '2007-03-27');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Geography of Girlhood', 35, 0, 184, 0, 0, 'Little Brown & Company', 'Bursting with painfully accurate and sharply witty observations, a novel written in verse follows a young woman exploring first crushes, first dances, first kisses, and the many other hazards of growing up.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1361038355i/428263.jpg', '9780316160210', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Vampires'', ''Fiction'', ''Paranormal'', ''Paranormal Romance'']', 'en', '2007-08-07');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Eldest', 36, 0, 706, 0, 0, 'Knopf Books for Young Readers', 'Don’t miss the eagerly anticipated epic new fantasy from Christopher Paolini—Murtagh! New magic and new threats take flight in Book Two of the Inheritance Cycle, perfect for fans of Lord of the Rings! This New York Times bestselling series has sold over 40 million copies and is an international fantasy sensation. "Christopher Paolini is a true rarity." —The Washington Post Darkness falls… despair abounds… evil reigns… Eragon and his dragon, Saphira, have just saved the rebel state from destruction by the mighty forces of King Galbatorix. Now Eragon must travel to Ellésmera, land of the elves, for further training in the skills of the Dragon Rider: magic and swordsmanship. But chaos and betrayal plague him at every turn, and nothing is what it seems. Before long, Eragon doesn’t know whom he can trust. Will the king’s dark hand strangle all resistance? Eragon may not escape with even his life. . . .', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1366212852i/113436.jpg', '9780375826700', '[''Fantasy'', ''Young Adult'', ''Fiction'', ''Dragons'', ''Adventure'', ''Magic'', ''High Fantasy'']', 'en', '2002-06-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Hitchhiker''s Guide to the Galaxy', 37, 0, 0, 0, 0, 'Del Rey', 'NEW YORK TIMES BESTSELLER • “Extremely funny . . . inspired lunacy . . . [and] over much too soon.”—The Washington Post Book World SOON TO BE A HULU SERIES • Now celebrating the pivotal 42nd anniversary of The Hitchhiker’s Guide to the Galaxy! Nominated as one of America’s best-loved novels by PBS’s The Great American Read It’s an ordinary Thursday morning for Arthur Dent . . . until his house gets demolished. The Earth follows shortly after to make way for a new hyperspace express route, and Arthur’s best friend has just announced that he’s an alien. After that, things get much, much worse. With just a towel, a small yellow fish, and a book, Arthur has to navigate through a very hostile universe in the company of a gang of unreliable aliens. Luckily the fish is quite good at languages. And the book is The Hitchhiker’s Guide to the Galaxy . . . which helpfully has the words DON’T PANIC inscribed in large, friendly letters on its cover. Douglas Adams’s mega-selling pop-culture classic sends logic into orbit, plays havoc with both time and physics, offers up pithy commentary on such things as ballpoint pens, potted plants, and digital watches . . . and, most important, reveals the ultimate answer to life, the universe, and everything. Now, if you could only figure out the question. . . .', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1531891848i/11.jpg', '9780345391800', '[''Science Fiction'', ''Fiction'', ''Humor'', ''Fantasy'', ''Comedy'', ''Audiobook'', ''Science Fiction Fantasy'']', 'en', '1979-10-12');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Tree Grows in Brooklyn', 38, 0, 430, 0, 0, '', 'The American classic about a young girl''s coming of age at the turn of the century.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1575509280i/5129.jpg', '9780060929880', '[''Fiction'', ''Science Fiction'', ''Dystopia'', ''Literature'', ''Novels'', ''School'', ''Audiobook'']', 'en', '1932-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Breaking Dawn', 2, 0, 768, 0, 0, 'Little, Brown Books for Young Readers', 'When you loved the one who was killing you, it left you no options. How could you run, how could you fight, when doing so would hurt that beloved one? If your life was all you had to give, how could you not give it? If it was someone you truly loved? To be irrevocably in love with a vampire is both fantasy and nightmare woven into a dangerously heightened reality for Bella Swan. Pulled in one direction by her intense passion for Edward Cullen, and in another by her profound connection to werewolf Jacob Black, a tumultuous year of temptation, loss, and strife have led her to the ultimate turning point. Her imminent choice to either join the dark but seductive world of immortals or to pursue a fully human life has become the thread from which the fates of two tribes hangs. Now that Bella has made her decision, a startling chain of unprecedented events is about to unfold with potentially devastating, and unfathomable, consequences. Just when the frayed strands of Bella''s life-first discovered in Twilight, then scattered and torn in New Moon and Eclipse-seem ready to heal and knit together, could they be destroyed... forever? The astonishing, breathlessly anticipated conclusion to the Twilight Saga, Breaking Dawn illuminates the secrets and mysteries of this spellbinding romantic epic that has entranced millions.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1361039438i/1162543.jpg', '9780316067930', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Vampires'', ''Fiction'', ''Paranormal'', ''Paranormal Romance'']', 'en', '2008-08-02');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Secret Life of Bees', 39, 0, 340, 0, 0, 'Penguin', 'The multi-million bestselling novel about a young girl''s journey towards healing and the transforming power of love, from the award-winning author of The Invention of Wings and The Book of Longings Set in South Carolina in 1964, The Secret Life of Bees tells the story of Lily Owens, whose life has been shaped around the blurred memory of the afternoon her mother was killed. When Lily''s fierce-hearted Black "stand-in mother," Rosaleen, insults three of the deepest racists in town, Lily decides to spring them both free. They escape to Tiburon, South Carolina—a town that holds the secret to her mother''s past. Taken in by an eccentric trio of Black beekeeping sisters, Lily is introduced to their mesmerizing world of bees and honey, and the Black Madonna. This is a remarkable novel about divine female power, a story that women will share and pass on to their daughters for years to come.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1628532099i/37435.jpg', '9780142001740', '[''Fiction'', ''Historical Fiction'', ''Book Club'', ''Classics'', ''Young Adult'', ''Historical'', ''Contemporary'']', 'en', '2001-11-08');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Siddhartha', 40, 0, 0, 0, 0, 'National Geographic Books', 'A bold translation of Nobel Prize-winner Herman Hesse''s most inspirational and beloved work, which was nominated as one of America’s best-loved novels by PBS’s The Great American Read A Penguin Classics Deluxe Edition Hesse''s famous and influential novel, Siddartha, is perhaps the most important and compelling moral allegory our troubled century has produced. Integrating Eastern and Western spiritual traditions with psychoanalysis and philosophy, this strangely simple tale, written with a deep and moving empathy for humanity, has touched the lives of millions since its original publication in 1922. Set in India, Siddhartha is the story of a young Brahmin''s search for ultimate reality after meeting with the Buddha. His quest takes him from a life of decadence to asceticism, through the illusory joys of sensual love with a beautiful courtesan, and of wealth and fame, to the painful struggles with his son and the ultimate wisdom of renunciation. This new translation by award-winning translator Joachim Neugroschel includes an introduction by Hesse biographer Ralph Freedman. For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1546096879i/2956.jpg', '9780142437180', '[''Fiction'', ''Classics'', ''Historical Fiction'', ''Literature'', ''Adventure'', ''Young Adult'', ''School'']', 'en', '1884-02-18');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Trumpet of the Swan (full color)', 41, 0, 276, 0, 0, 'Harper Collins', 'Swan Song Like the rest of his family, Louis is a trumpeter swan. But unlike his four brothers and sisters, Louis can''t trumpet joyfully. In fact, he can''t even make a sound. And since he can''t trumpet his love, the beautiful swan Serena pays absolutely no attention to him. Louis tries everything he can think of to win Serena''s affection--he even goes to school to learn to read and write. But nothing seems to work. Then his father steals him a real brass trumpet. Is a musical instrument the key to winning Louis his love?', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1628267712i/24178.jpg', '9780064410940', '[''Fiction'', ''Childrens'', ''Classics'', ''Fantasy'', ''Young Adult'', ''Middle Grade'', ''Animals'']', 'en', '1952-10-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('How the Universe Got Its Spots', 42, 0, 225, 0, 0, 'Anchor', 'A physicist describes what we know about the shape, extent, origins, and evolution of the universe, the complexities of space and time, and the secrets of black holes, time warps, and other phenomena.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1479863624i/1618.jpg', '9781400032720', '[''Fiction'', ''Mystery'', ''Young Adult'', ''Book Club'', ''Classics'', ''Autistic Spectrum Disorder'', ''Novels'']', 'en', '2003-07-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('His Dark Materials: The Golden Compass (Book 1)', 43, 0, 418, 0, 0, 'Knopf Books for Young Readers', 'The modern fantasy classic that Entertainment Weekly named an “All-Time Greatest Novel” and Newsweek hailed as a “Top 100 Book of All Time.” Philip Pullman takes readers to a world where humans have animal familiars and where parallel universes are within reach. Lyra is rushing to the cold, far North, where witch clans and armored bears rule. North, where the Gobblers take the children they steal--including her friend Roger. North, where her fearsome uncle Asriel is trying to build a bridge to a parallel world. Can one small girl make a difference in such great and terrible endeavors? This is Lyra: a savage, a schemer, a liar, and as fierce and true a champion as Roger or Asriel could want. But what Lyra doesn''t know is that to help one of them will be to betray the other... A masterwork of storytelling and suspense, Philip Pullman''s award-winning The Golden Compass is the first in the His Dark Materials series, which continues with The Subtle Knife and The Amber Spyglass. A #1 New York Times Bestseller Winner of the Guardian Prize for Children''s Fiction Published in 40 Countries "Arguably the best juvenile fantasy novel of the past twenty years." —The Washington Post "Very grand indeed." —The New York Times "Pullman is quite possibly a genius." —Newsweek Don''t miss Philip Pullman''s epic new trilogy set in the world of His Dark Materials! ** THE BOOK OF DUST ** La Belle Sauvage The Secret Commonwealth', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1505766203i/119322.jpg', '9780679879240', '[''Fantasy'', ''Young Adult'', ''Fiction'', ''Childrens'', ''Adventure'', ''Middle Grade'', ''Audiobook'']', 'en', '1995-07-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Discover Biology', 44, 0, 271, 0, 0, '', '', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388212715i/6185.jpg', '9780393978900', '[''Fiction'', ''Gothic'', ''Historical Fiction'', ''Literature'', ''Historical'', ''Novels'', ''19th Century'']', 'en', '1847-12-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Vanishing Acts', 45, 0, 432, 0, 0, 'Atria Books', 'New York Times bestselling author Jodi Picoult is widely acclaimed for her ability to tap into the hearts and minds of real people. Now she explores what happens when a young woman''s past -- a past she didn''t even know she had -- catches up to her just in time to threaten her future. Delia Hopkins has led a charmed life. Raised in rural New Hampshire by her widowed father, Andrew, she now has a young daughter, a handsome fiancé, and her own search-and-rescue bloodhound, which she uses to find missing persons. But as Delia plans her wedding, she is plagued by flashbacks of a life she can''t recall. And then a policeman knocks on her door, revealing a secret that changes the world as she knows it. In shock and confusion, Delia must sift through the truth -- even when it jeopardizes her life and the lives of those she loves. What happens when you learn you are not who you thought you were? When the people you''ve loved and trusted suddenly change before your eyes? When getting your deepest wish means giving up what you''ve always taken for granted? Vanishing Acts explores how life -- as we know it -- might not turn out the way we imagined; how doing the right thing could mean doing the wrong thing; how the memory we thought had vanished could return as a threat. Once again, Jodi Picoult handles a difficult and timely topic with understanding, insight, and compassion.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1369504683i/10917.jpg', '9780743454540', '[''Fiction'', ''Book Club'', ''Chick Lit'', ''Young Adult'', ''Drama'', ''Adult'', ''Adult Fiction'']', 'en', '2004-04-06');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Woody Guthrie', 46, 0, 532, 0, 0, 'Delta', 'A biography of the influential American folk singer, Woody Guthrie, who lived a life on the edge of tragedy but inspired a generation of songwriters, including Bruce Springsteen and Bob Dylan. Few artists have captured the American experience of their time as wholly as folk legend Woody Guthrie. Singer, songwriter, and political activist, Guthrie drew a lifetime of inspiration from his roots on the Oklahoma frontier in the years before the Great Depression. His music—scathingly funny songs and poignant folk ballads—made heard the unsung life of field hands, migrant workers, and union organizers, and showed it worthy of tribute. Though his career was tragically cut short by the onset of a degenerative disease that ravaged his mind and body, the legacy of his life and music had already made him an American cultural icon, and has resounded with every generation of musician and music lover since. In this definitive biography, Joe Klein, nationally renowned journalist and author of the bestselling novel Primary Colors, creates an unforgettable portrait of a man as gifted, restless, and complicated as the American landscape he came from. Praise for Woody Guthrie: A Life “One of the finest treatments of an American 20th-century performer ever written . . . Not merely a biography . . . it is a social history . . . written knowledgeably, in a brilliant style.”—San Francisco Examiner “A really great book.”—Bruce Springsteen', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1440319389i/4981.jpg', '9780385333850', '[''Fiction'', ''Science Fiction'', ''Classics'', ''Historical Fiction'', ''War'', ''Literature'', ''Novels'']', 'en', '1969-03-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Knock at Midnight', 47, 0, 256, 0, 0, 'Grand Central Publishing', 'With fiery words of wisdom and a passion for justice, Reverend Martin Luther King, Jr., inspired people everywhere to perform extraordinary acts of courage and ignited one of the most influential movements of the twentieth century. This is the definitive collection of eleven of his most powerful sermons, from his earliest known audio recording to his last sermon, delivered days before his assassination. With introductions by renowned theologians and ministers including Reverend Billy Graham and Archbishop Desmond Tutu, filled with moving personal reflections and firsthand accounts of the events surrounding each sermon, A KNOCK AT MIDNIGHT is Dr. King''s living voice today -- an irresistible call that resonates and inspires the greatness in us all.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1551144577i/18405.jpg', '9780446675540', '[''Classics'', ''Historical Fiction'', ''Fiction'', ''Romance'', ''Historical'', ''War'', ''Literature'']', 'en', '1936-06-30');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Thousand Splendid Suns', 10, 0, 0, 0, 0, 'National Geographic Books', 'Propelled by the same superb instinct for storytelling that made The Kite Runner a beloved classic, the #1 New York Times bestseller A Thousand Splendid Suns is at once an incredible chronicle of thirty years of Afghan history and a deeply moving story of family, friendship, faith, and the salvation to be found in love. “Just as good, if not better, than Khaled Hosseini’s best-selling first book, The Kite Runner.”—Newsweek Khaled Hosseini returns with a beautiful, riveting, and haunting novel that confirms his place as one of the most important literary writers today. Born a generation apart and with very different ideas about love and family, Mariam and Laila are two women brought jarringly together by war, by loss and by fate. As they endure the ever escalating dangers around them-in their home as well as in the streets of Kabul-they come to form a bond that makes them both sisters and mother-daughter to each other, and that will ultimately alter the course not just of their own lives but of the next generation. With heart-wrenching power and suspense, Hosseini shows how a woman''s love for her family can move her to shocking and heroic acts of self-sacrifice, and that in the end it is love, or even the memory of love, that is often the key to survival. A stunning accomplishment, A Thousand Splendid Suns is a haunting, heartbreaking, compelling story of an unforgiving time, an unlikely friendship, and an indestructible love.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1655336738i/128029.jpg', '9781594489500', '[''Fiction'', ''Historical Fiction'', ''Book Club'', ''Historical'', ''Classics'', ''Novels'', ''War'']', 'en', '2007-05-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Angels & Demons', 8, 0, 456, 0, 0, 'Beyond Words/Atria Books', 'World renowned harvard symbologist Robert Langdon must uncover clues that point to the resurgance of an ancient secret society.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1650033115i/22628.jpg', '9780671027350', '[''Young Adult'', ''Fiction'', ''Classics'', ''Coming Of Age'', ''Romance'', ''Mental Health'', ''Realistic Fiction'']', 'en', '1999-02-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Ender''s Game', 49, 0, 358, 0, 0, 'Tor Science Fiction', 'From New York Times bestselling author Orson Scott Card, Ender''s Game—adapted to film in 2013 starring Asa Butterfield and Harrison Ford—is the classic Hugo and Nebula award-winning science fiction novel of a young boy''s recruitment into the midst of an interstellar war. In order to develop a secure defense against a hostile alien race''s next attack, government agencies breed child geniuses and train them as soldiers. A brilliant young boy, Andrew "Ender" Wiggin lives with his kind but distant parents, his sadistic brother Peter, and the person he loves more than anyone else, his sister Valentine. Peter and Valentine were candidates for the soldier-training program but didn''t make the cut—young Ender is the Wiggin drafted to the orbiting Battle School for rigorous military training. Ender''s skills make him a leader in school and respected in the Battle Room, where children play at mock battles in zero gravity. Yet growing up in an artificial community of young soldiers Ender suffers greatly from isolation, rivalry from his peers, pressure from the adult teachers, and an unsettling fear of the alien invaders. His psychological battles include loneliness, fear that he is becoming like the cruel brother he remembers, and fanning the flames of devotion to his beloved sister. Is Ender the general Earth needs? But Ender is not the only result of the genetic experiments. The war with the Buggers has been raging for a hundred years, and the quest for the perfect general has been underway for almost as long. Ender''s two older siblings are every bit as unusual as he is, but in very different ways. Between the three of them lie the abilities to remake a world. If, that is, the world survives. Orson Scott Card''s Ender''s Game is the winner of the 1985 Nebula Award for Best Novel and the 1986 Hugo Award for Best Novel. THE ENDER UNIVERSE Ender series Ender’s Game / Ender in Exile / Speaker for the Dead / Xenocide / Children of the Mind Ender’s Shadow series Ender’s Shadow / Shadow of the Hegemon / Shadow Puppets / Shadow of the Giant / Shadows in Flight Children of the Fleet The First Formic War (with Aaron Johnston) Earth Unaware / Earth Afire / Earth Awakens The Second Formic War (with Aaron Johnston) The Swarm /The Hive Ender novellas A War of Gifts /First Meetings', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1408303130i/375802.jpg', '9780812550700', '[''Science Fiction'', ''Fiction'', ''Young Adult'', ''Fantasy'', ''Classics'', ''Science Fiction Fantasy'', ''Dystopia'']', 'en', '1985-01-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Frankenstein', 50, 0, 0, 0, 0, 'Penguin Classics', 'The world’s most famous work of horror fiction: a devastating exploration of the limits of human creativity. Nominated as one of America’s best-loved novels by PBS’s The Great American Read Mary Shelley''s timeless gothic novel presents the epic battle between man and monster at its greatest literary pitch. In trying to create life, the young student Victor Frankenstein unleashes forces beyond his control, setting into motion a long and tragic chain of events that brings Victor to the very brink of madness. How he tries to destroy his creation, as it destroys everything Victor loves, is a powerful story of love, friendship, scientific hubris, and horror. Based on the third edition of 1831, this Penguin Classics edition, with an introduction and notes by Maurice Hindle, contains all the revisions Mary Shelley made to her story, as well as her 1831 introduction and Percy Bysshe Shelley’s preface to the first edition. It also includes as appendices a select collation of the texts of 1818 and 1831 together with "A Fragment" by Lord Byron and Dr John Polidori’s "The Vampyre: A Tale." For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1665008650i/18490.jpg', '9780141439470', '[''Horror'', ''Fiction'', ''Science Fiction'', ''Gothic'', ''Fantasy'', ''School'', ''Literature'']', 'en', '1818-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Shining', 51, 0, 416, 0, 0, '', 'Danny is only five years old, but he is a ''shiner'', aglow with psychic voltage. When his father becomes caretaker of an old hotel, his visions grow out of control. Cut off by blizzards, the hotel seems to develop an evil force, and who are the mysterious guests in the supposedly empty hotel?', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1353277730i/11588.jpg', '9780450040180', '[''Horror'', ''Fiction'', ''Thriller'', ''Classics'', ''Fantasy'', ''Paranormal'', ''Mystery'']', 'en', '1977-01-28');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Looking for Alaska', 5, 0, 274, 0, 0, 'Penguin', 'The award-winning, genre-defining debut from John Green, the #1 bestselling author of The Anthropocene Reviewed and The Fault in Our Stars Winner of the Michael L. Printz Award • A Los Angeles Times Book Prize Finalist • A New York Times Bestseller • A USA Today Bestseller • NPR’s Top Ten Best-Ever Teen Novels • TIME magazine’s 100 Best Young Adult Novels of All Time • A PBS Great American Read Selection • Millions of copies sold! First drink. First prank. First friend. First love. Last words. Miles Halter is fascinated by famous last words—and tired of his safe life at home. He leaves for boarding school to seek what the dying poet François Rabelais called the “Great Perhaps.” Much awaits Miles at Culver Creek, including Alaska Young, who will pull Miles into her labyrinth and catapult him into the Great Perhaps. Looking for Alaska brilliantly chronicles the indelible impact one life can have on another. A modern classic, this stunning debut marked #1 bestselling author John Green’s arrival as a groundbreaking new voice in contemporary fiction. Newly updated edition includes a brand-new Readers'' Guide featuring a Q&A with author John Green', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1652042180i/99561.jpg', '9780142402510', '[''Young Adult'', ''Fiction'', ''Contemporary'', ''Romance'', ''Realistic Fiction'', ''Coming Of Age'', ''Teen'']', 'en', '2005-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Sense and Sensibility', 52, 0, 454, 0, 0, 'Penguin', 'Jane Austen''s first published work, meticulously constructed and sparkling with her unique wit Marianne Dashwood wears her heart on her sleeve, and when she falls in love with the dashing but unsuitable John Willoughby she ignores her sister Elinor''s warning that her impulsive behaviour leaves her open to gossip and innuendo. Meanwhile Elinor, always sensitive to social convention, is struggling to conceal her own romantic disappointment, even from those closest to her. Through their parallel experience of love - and its threatened loss - the sisters learn that sense must mix with sensibility if they are to find personal happiness in a society where status and money govern the rules of love. This edition also includes explanatory notes and textual variants between first and second edition. For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1397245675i/14935.jpg', '9780141439660', '[''Fiction'', ''Historical Fiction'', ''Literature'', ''Historical'', ''Audiobook'', ''19th Century'', ''Classic Literature'']', 'en', '1811-10-30');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Holes', 53, 0, 244, 0, 0, 'Scholastic', 'As further evidence of his family''s bad fortune which they attribute to a curse on a distant relative, Stanley Yelnats is sent to a hellish correctional camp in the Texas desert where he finds his first real friend, a treasure, and a new sense of himself.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1618269830i/38709.jpg', '9780439244190', '[''Young Adult'', ''Fiction'', ''Middle Grade'', ''Childrens'', ''Classics'', ''School'', ''Realistic Fiction'']', 'en', '1998-08-20');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Devil Wears Prada', 54, 0, 450, 0, 0, 'Anchor', 'A small-town girl fresh out of an Ivy League college lands a job at a prestigious fashion magazine, but wonders if the glamorous perks are worth working for the editor from hell.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388179604i/5139.jpg', '9780307275550', '[''Chick Lit'', ''Fiction'', ''Contemporary'', ''Romance'', ''Humor'', ''Adult'', ''Adult Fiction'']', 'en', '2003-04-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Odyssey', 55, 0, 572, 0, 0, 'Penguin Classics', 'When Robert Fagles'' translation of the Iliad was published in 199, critics and scholars alike hailed it as a masterpiece. Now one of the great translators of our time presents us with the Odyssey, Homer''s best-loved poem, recounting Odysseus'' wanderings after the Trojan War. With wit and wile, the ''man of twists and turns'' meets the challenges of gods and monsters, only to return after twenty years to a home besieged by his wife''s suitors. In the myths and legends retold in this immortal poem, Fagles has captured the energy of Homer''s original in a bold, contemporary idiom.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1711957706i/1381.jpg', '9780143039950', '[''Fiction'', ''Poetry'', ''Mythology'', ''Fantasy'', ''School'', ''Literature'', ''Adventure'']', 'en', '2006-10-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Glass Castle', 56, 0, 304, 0, 0, 'Scribner', 'Now a major motion picture from Lionsgate starring Brie Larson, Woody Harrelson, and Naomi Watts. MORE THAN SEVEN YEARS ON THE NEW YORK TIMES BESTSELLER LIST The perennially bestselling, extraordinary, one-of-a-kind, “nothing short of spectacular” (Entertainment Weekly) memoir from one of the world’s most gifted storytellers. The Glass Castle is a remarkable memoir of resilience and redemption, and a revelatory look into a family at once deeply dysfunctional and uniquely vibrant. When sober, Jeannette’s brilliant and charismatic father captured his children’s imagination, teaching them physics, geology, and how to embrace life fearlessly. But when he drank, he was dishonest and destructive. Her mother was a free spirit who abhorred the idea of domesticity and didn’t want the responsibility of raising a family. The Walls children learned to take care of themselves. They fed, clothed, and protected one another, and eventually found their way to New York. Their parents followed them, choosing to be homeless even as their children prospered. The Glass Castle is truly astonishing—a memoir permeated by the intense love of a peculiar but loyal family.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1523542886i/7445.jpg', '9780743247540', '[''Nonfiction'', ''Memoir'', ''Book Club'', ''Biography'', ''Biography Memoir'', ''Autobiography'', ''Audiobook'']', 'en', '2005-03-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Into the Wild', 57, 0, 228, 0, 0, 'Villard', 'In April 1992, a young man from a well-to-do family hitchhikes to Alaska and walks alone into the wilderness north of Mt. McKinley. Four months later, his decomposed body is found by a moose hunter. How Chris McCandless came to die is the unforgettable story of Into the Wild.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1634587789i/1845.jpg', '9780385486800', '[''Nonfiction'', ''Biography'', ''Adventure'', ''Classics'', ''Memoir'', ''Nature'', ''School'']', 'en', '1996-01-13');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Tale of Two Cities', 58, 0, 546, 0, 0, 'Penguin', '''It was the best of times, it was the worst of times...'' Charles Dickens''s A Tale of Two Cities portrays a world on fire, split between Paris and London during the brutal and bloody events of the French Revolution. After eighteen years as a political prisoner in the Bastille the aging Dr Manette is finally released and reunited with his daughter in England. There, two very different men, Charles Darnay, an exiled French aristocrat, and Sydney Carton, a disreputable but brilliant English lawyer, become enmeshed through their love for Lucie Manette. From the tranquil lanes of London, they are all drawn against their will to the vengeful, bloodstained streets of Paris at the height of the Reign of Terror and soon fall under the lethal shadow of La Guillotine. This edition uses the text as it appeared in its first serial publication in 1859 to convey the full scope of Dickens''s vision, and includes the original illustrations by H.K. Browne (''Phiz''). Richard Maxwell''s introduction discusses the intricate interweaving of epic drama with personal tragedy. For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1344922523i/1953.jpg', '9780141439600', '[''Fiction'', ''Historical Fiction'', ''Literature'', ''Historical'', ''Novels'', ''School'', ''Classic Literature'']', 'en', '1859-11-26');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Mi casé con mi empleado', 59, 0, 0, 0, 0, 'Plaza y Janes', 'Glynis Wolgers es una joven trabajadora. Hija de padres divorciados y vueltos a casar, ha quedado sola, pero sale adelante gracias a su fuerza de voluntad y su talento como diseñadora de modas. Trabaja para varias revistas diseñando modelos, con lo que puede darse pequeños lujos: pagarse su propio piso, su servicio doméstico y su ropa. Sostiene una curiosa relación sentimental con un tal Robert, pero sin duda éste no es el hombre de su vida. Le resulta poderosamente aburrido, aunque no se decide a dejarlo. Un día su criada le comunica que ya no podrá hacerse cargo de la casa, pues su hija ha tenido familia nuevamente y debe ayudarla. Por lo tanto, Glynis pide un reemplazo a una agencia de empleos. Sorprendentemente, le envían a un hombre. Ella, al principio, se niega a contratarlo, pero luego, debido a la notoria eficiencia del nuevo empleado, lo acepta. Paulatinamente, van enamorándose el uno del otro, aunque nada se dicen. Algunas miradas, pequeños comentarios van acercándolos. Una noche él le comenta que saldrá a dar una vuelta. Ella, tiempo después, decide hacer lo mismo. Por casualidad se encuentran en un bar y deciden fingir que no se conocen. Pasan una noche maravillosa juntos, tras la cual él le propone matrimonio. Aunque al principio duda, Glynis acepta y se casan en secreto. Poco a poco, los parientes de ella y los de él, que al final resulta ser el hijo de un millonario que ha escapado de su casa porque no lo dejaban ser él mismo y estudiar lo que quería, aceptan el matrimonio y ambos viven felices el resto de sus vidas.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1740737452i/7677.jpg', '9780307348140', '[''Science Fiction'', ''Fiction'', ''Thriller'', ''Horror'', ''Fantasy'', ''Adventure'', ''Classics'']', 'es', '1990-11-07');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Giving Tree', 33, 0, 64, 0, 0, 'HarperCollins Publishers', '''Once there was a tree...and she loved a little boy.'' So begins a story of unforgettable perception, beautifully written and illustrated by the gifted and versatile Shel Silverstein. Every day the boy would come to the tree to eat her apples, swing from her branches, or slide down her trunk...and the tree was happy. But as the boy grew older he began to want more from the tree, and the tree gave and gave and gave. This is a tender story, touched with sadness, aglow with consolation. Shel Silverstein has created a moving parable for readers of all ages that offers an affecting interpretation of the gift of giving and a serene acceptance of another''s capacity to love in return. Ages 10+', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1725807591i/370493.jpg', '9780060256650', '[''Childrens'', ''Classics'', ''Fiction'', ''Picture Books'', ''Poetry'', ''Young Adult'', ''Fantasy'']', 'en', '1964-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Da Vinci Decoded', 60, 0, 0, 0, 0, 'Random House Digital, Inc.', 'From art to botany, anatomy to mechanics, Da Vinci was a profoundly original thinker fully in tune with the world of man and nature, and with the divine spirit that bridges the two.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1554303029i/32542.jpg', '9780385338610', '[''Fiction'', ''Thriller'', ''Mystery'', ''Crime'', ''Suspense'', ''Legal Thriller'', ''Mystery Thriller'']', 'en', '1989-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Paper Towns', 5, 0, 337, 0, 0, 'Penguin', 'From the #1 bestselling author of Turtles All the Way Down and The Fault in Our Stars Winner of the Edgar Award for Best Young Adult Mystery #1 New York Times Bestseller USA Today Bestseller Publishers Weekly Bestseller Now a major motion picture When Margo Roth Spiegelman beckons Quentin Jacobsen in the middle of the night—dressed like a ninja and plotting an ingenious campaign of revenge—he follows her. Margo’s always planned extravagantly, and, until now, she’s always planned solo. After a lifetime of loving Margo from afar, things are finally looking up for Q . . . until day breaks and she has vanished. Always an enigma, Margo has now become a mystery. But there are clues. And they’re for Q. Printz Medalist John Green returns with the trademark brilliant wit and heart-stopping emotional honesty that have inspired a new generation of readers.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1349013610i/6442769.jpg', '9780142414930', '[''Young Adult'', ''Fiction'', ''Contemporary'', ''Romance'', ''Mystery'', ''Realistic Fiction'', ''Coming Of Age'']', 'en', '2008-10-16');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Princess Bride', 61, 0, 0, 0, 0, '', 'What happens when the most beautiful girl in the world marries the handsomest prince of all time and he turns out to be...well...a lot less than the man of her dreams? As a boy, William Goldman claims, he loved to hear his father read the "S. Morgenstern classic, The Princess Bride. But as a grown-up he discovered that the boring parts were left out of good old Dad''s recitation, and only the "good parts" reached his ears. Now Goldman does Dad one better. He''s reconstructed the "Good Parts Version" to delight wise kids and wide-eyed grownups everywhere. What''s it about? Fencing. Fighting. True Love. Strong Hate. Harsh Revenge. A Few Giants. Lots of Bad Men. Lots of Good Men. Five or Six Beautiful Women. Beasties Monstrous and Gentle. Some Swell Escapes and Captures. Death, Lies, Truth, Miracles, and a Little Sex. In short, it''s about everything. Eventually to be adapted for the silver screen, THE PRINCESS BRIDE was originally a beautifully simple, insightfully comic story of what happens when the most beautiful girl in the world marries the handsomest prince in the world--and he turnsout to be a son of a bitch. Guaranteed to entertain both young and old alike by combining scenes of rowsing fantasy with hilarious reality, THE PRINCESS BRIDE secures Goldman''s place as a master storyteller. From the Paperback edition.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327903636i/21787.jpg', '9780345418260', '[''Fantasy'', ''Fiction'', ''Romance'', ''Humor'', ''Adventure'', ''Young Adult'', ''Classics'']', 'en', '1973-09-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Outsiders', 62, 0, 0, 0, 0, 'National Geographic Books', 'The 45th anniversary of a landmark work of teen fiction Ponyboy can count on his brothers and his friends, but not on much else besides trouble with the Socs, a vicious gang of rich kids who get away with everything, including beating up greasers like Ponyboy. At least he knows what to expect--until the night someone takes things too far. Written forty-five years ago, S. E. Hinton''s classic story of a boy who finds himself on the outskirts of regular society remains as powerful today as it was the day it was written.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1442129426i/231804.jpg', '9780140385720', '[''Classics'', ''Young Adult'', ''Fiction'', ''School'', ''Read For School'', ''Coming Of Age'', ''Realistic Fiction'']', 'en', '1967-04-24');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Maze Runner', 63, 0, 401, 0, 0, 'Delacorte Press', 'THE #1 NEW YORK TIMES BESTSELLING MAZE RUNNER SERIES • A teenager with no memory must navigate a deadly maze to survive in book one of this post-apocalyptic phenomenon. “[A] mysterious survival saga that passionate fans describe as a fusion of Lord of the Flies [and] The Hunger Games” (Entertainment Weekly) When Thomas wakes up in the lift, the only thing he can remember is his name. He’s surrounded by strangers—boys whose memories are also gone. Outside the towering stone walls that surround them is a limitless, ever-changing maze. It’s the only way out—and no one’s ever made it through alive. Then a girl arrives. The first girl ever. And the message she delivers is terrifying: Remember. Survive. Run. Look for more books in the blockbuster Maze Runner series: THE MAZE RUNNER • THE SCORCH TRIALS • THE DEATH CURE • THE KILL ORDER • THE FEVER CODE', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1375596592i/6186357.jpg', '9780385737940', '[''Young Adult'', ''Dystopia'', ''Science Fiction'', ''Fiction'', ''Fantasy'', ''Audiobook'', ''Post Apocalyptic'']', 'en', '2009-10-06');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Freakonomics Rev Ed', 64, 0, 340, 0, 0, 'Harper Collins', 'Which is more dangerous, a gun or a swimming pool? What do schoolteachers and sumo wrestlers have in common? Why do drug dealers still live with their moms? How much do parents really matter? How did the legalization of abortion affect the rate of violent crime? These may not sound like typical questions for an econo-mist to ask. But Steven D. Levitt is not a typical economist. He is a much-heralded scholar who studies the riddles of everyday life—from cheating and crime to sports and child-rearing—and whose conclusions turn conventional wisdom on its head. Freakonomics is a groundbreaking collaboration between Levitt and Stephen J. Dubner, an award-winning author and journalist. They usually begin with a mountain of data and a simple question. Some of these questions concern life-and-death issues; others have an admittedly freakish quality. Thus the new field of study contained in this book: freakonomics. Through forceful storytelling and wry insight, Levitt and Dubner show that economics is, at root, the study of incentives—how people get what they want, or need, especially when other people want or need the same thing. In Freakonomics, they explore the hidden side of . . . well, everything. The inner workings of a crack gang. The truth about real-estate agents. The myths of campaign finance. The telltale marks of a cheating schoolteacher. The secrets of the Klu Klux Klan. What unites all these stories is a belief that the modern world, despite a great deal of complexity and downright deceit, is not impenetrable, is not unknowable, and—if the right questions are asked—is even more intriguing than we think. All it takes is a new way of looking. Freakonomics establishes this unconventional premise: If morality represents how we would like the world to work, then economics represents how it actually does work. It is true that readers of this book will be armed with enough riddles and stories to last a thousand cocktail parties. But Freakonomics can provide more than that. It will literally redefine the way we view the modern world.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1550917827i/1202.jpg', '9780061234000', '[''Nonfiction'', ''Economics'', ''Business'', ''Science'', ''Psychology'', ''Sociology'', ''Finance'']', 'en', '2005-04-12');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Secret Garden', 66, 0, 290, 0, 0, '', 'The Secret Garden, a classic loved for more than seventy-five years, is all that its title implies. It is, as one might imagine, a mystery. But it is also a love story and the love is as passionate and fervent as one could imagine. It is a story of the love of nature, of science and the scientific method, and of isolated and lonely human beings who learn to care not only for others, but equally important, for themselves. This deluxe Children''s Classic edition is produced with high-quality, leatherlike binding with gold stamping, full-color covers, colored endpapers with a book nameplate. Some of the other titles in this series include: Anne of Green Gables, Black Beauty, King Arthur and His Knights, Little Women, and Treasure Island.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327873635i/2998.jpg', '9780517189600', '[''Fiction'', ''Classics'', ''Childrens'', ''Young Adult'', ''Historical Fiction'', ''Middle Grade'', ''Audiobook'']', 'en', '1911-08-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('One Hundred Years of Solitude', 68, 0, 433, 0, 0, 'Harper Collins', 'One of the 20th century''s enduring works, One Hundred Years of Solitude is a widely beloved and acclaimed novel known throughout the world, and the ultimate achievement in a Nobel Prize–winning career. The novel tells the story of the rise and fall of the mythical town of Macondo through the history of the Buendía family. It is a rich and brilliant chronicle of life and death, and the tragicomedy of humankind. In the noble, ridiculous, beautiful, and tawdry story of the Buendía family, one sees all of humanity, just as in the history, myths, growth, and decay of Macondo, one sees all of Latin America. Love and lust, war and revolution, riches and poverty, youth and senility -- the variety of life, the endlessness of death, the search for peace and truth -- these universal themes dominate the novel. Whether he is describing an affair of passion or the voracity of capitalism and the corruption of government, Gabriel García Márquez always writes with the simplicity, ease, and purity that are the mark of a master. Alternately reverential and comical, One Hundred Years of Solitude weaves the political, personal, and spiritual to bring a new consciousness to storytelling. Translated into dozens of languages, this stunning work is no less than an accounting of the history of the human race.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327881361i/320.jpg', '9780060531040', '[''Fiction'', ''Magical Realism'', ''Fantasy'', ''Literature'', ''Historical Fiction'', ''Novels'', ''Book Club'']', 'en', '1967-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Hunting Trips of a Ranchman', 69, 0, 840, 0, 0, '', 'Written during his days as a ranchman in the Dakota Bad Lands, these two wilderness tales by Theodore Roosevelt endure today as part of the classic folklore of the West. The narratives provide vivid portraits of the land as well as the people and animals that inhabited it, underscoring Roosevelt''s abiding concerns as a naturalist. Originally published in 1885, Hunting Trips of a Ranchman chronicles Roosevelt''s adventures tracking a twelve-hundred-pound grizzly bear in the pine forests of the Bighorn Mountains. Yet some of the best sections are those in which Roosevelt muses on the beauty of the Bad Lands and the simple pleasures of ranch life. The Wilderness Hunter, which came out in 1893, remains perhaps the most detailed account of the grizzly bear ever recorded and an intensely personal self-portrait of the man who would be president.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1546103428i/5297.jpg', '9780375751520', '[''Classics'', ''Fiction'', ''Horror'', ''Gothic'', ''Fantasy'', ''Literature'', ''Dark Academia'']', 'en', '1890-06-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('McTeague', 70, 0, 440, 0, 0, 'W. W. Norton', 'The text of this edition presents, fully annotated, the 1899 First Edition text of McTeague, a significant example of American literary naturalism and a commentary on turn-of-the-century American cultural values.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1387151694i/17245.jpg', '9780393970130', '[''Horror'', ''Fiction'', ''Fantasy'', ''Gothic'', ''Paranormal'', ''Literature'', ''Vampires'']', 'en', '1897-05-26');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Fifty Shades Darker', 71, 0, 383, 0, 0, 'Lulu.com', 'After a brief break-up, Anastasia and Christian change their arrangement and both wrestle with their inner demons, as Anastasia tries to make an important decision.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1631260114i/11857408.jpg', '9781612130580', '[''Romance'', ''Fiction'', ''Erotica'', ''BDSM'', ''Adult'', ''Contemporary'', ''Contemporary Romance'']', 'en', '2011-09-16');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Poisonwood Bible', 72, 0, 578, 0, 0, 'Harper Collins', 'The Poisonwood Bible is a story told by the wife and four daughters of Nathan Price, a fierce, evangelical Baptist who takes his family and mission to the Belgian Congo in 1959. They carry with them everything they believe they will need from home, but soon find that all of it -- from garden seeds to Scripture -- is calamitously transformed on African soil. What follows is a suspenseful epic of one family''s tragic undoing and remarkable reconstruction over the course of three decades in postcolonial Africa. This P.S. edition features an extra 16 pages of insights into the book, including author interviews, recommended reading, and more.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1644073807i/7244.jpg', '9780060786500', '[''Fiction'', ''Historical Fiction'', ''Africa'', ''Book Club'', ''Classics'', ''Literary Fiction'', ''Historical'']', 'en', '1998-09-24');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Me Talk Pretty One Day', 73, 0, 272, 0, 0, 'Gardners Books', 'David Sedaris moved from New York to Paris where he attempted to learn French. His teacher, a sadist, declared that every day spent with him was like giving birth the Caesarean way! These hilarious essays were inspired by that move.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1431013639i/4137.jpg', '9780349113910', '[''Nonfiction'', ''Memoir'', ''Essays'', ''Short Stories'', ''Comedy'', ''Humor'', ''Audiobook'']', 'en', '2000-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Symposium', 74, 0, 0, 0, 0, 'Penguin', 'A fascinating discussion on sex, gender, and human instincts, as relevant today as ever In the course of a lively drinking party, a group of Athenian intellectuals exchange views on eros, or desire. From their conversation emerges a series of subtle reflections on gender roles, sex in society and the sublimation of basic human instincts. The discussion culminates in a radical challenge to conventional views by Plato''s mentor, Socrates, who advocates transcendence through spiritual love. The Symposium is a deft interweaving of different viewpoints and ideas about the nature of love—as a response to beauty, a cosmic force, a motive for social action and as a means of ethical education. For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1724863997i/7126.jpg', '9780140449270', '[''Classics'', ''Fiction'', ''Historical Fiction'', ''Adventure'', ''Literature'', ''France'', ''Historical'']', 'en', '1846-01-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Road', 75, 0, 241, 0, 0, 'Knopf', 'In a novel set in an indefinite, futuristic, post-apocalyptic world, a father and his young son make their way through the ruins of a devastated American landscape, struggling to survive and preserve the last remnants of their own humanity. 250,000 first printing.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1600241424i/6288.jpg', '9780307265430', '[''Fiction'', ''Science Fiction'', ''Dystopia'', ''Post Apocalyptic'', ''Horror'', ''Book Club'', ''Literature'']', 'en', '2006-09-26');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Allegiant', 76, 0, 544, 0, 0, 'HarperCollins Children''s Books', 'The faction-based society that Tris Prior once believed in is shattered - fractured by violence and power struggles and scarred by loss and betrayal. So when offered a chance to explore the world past the limits she''s known, Tris is ready. Perhaps beyond the fence, she and Tobias will find a simple new life together, free from complicated lies, tangled loyalties, and painful memories. But Tris''s new reality is even more alarming than the one she left behind. Old discoveries are quickly rendered meaningless. Explosive new truths change the hearts of those she loves. And once again, Tris must battle to comprehend the complexities of human nature - and of herself - while facing impossible choices about courage, allegiance, sacrifice and love.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1395582745i/18710190.jpg', '9780007524270', '[''Young Adult'', ''Dystopia'', ''Fiction'', ''Fantasy'', ''Science Fiction'', ''Romance'', ''Adventure'']', 'en', '2013-10-22');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Book of the Film of the Story of My Life', 77, 0, 336, 0, 0, 'Grand Central Publishing', 'Originally published in New Zealand and in the UK, Brandt''s hilarious first novel about life, love, and the film business delves into the mind and mystery of the modern adult male.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1385738968i/3473.jpg', '9780446693810', '[''Romance'', ''Fiction'', ''Contemporary'', ''Young Adult'', ''Chick Lit'', ''Adult'', ''Contemporary Romance'']', 'en', '1999-10-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Devil''s Banker', 78, 0, 482, 0, 0, 'Dell', 'Hailed as “the John Grisham of Wall Street” by the New York Times, Christopher Reich returns to the world he knows so well--the dangerous, dazzling world of high finance and international intrigue. In this ingeniously crafted thriller, the bestselling author of Numbered Account and The First Billion introduces his most complex and engaging hero yet: forensic accountant Adam Chapel--and paints a frightening scenario where terrorism is big business and money is the ultimate weapon of war… The explosion that shatters the smart Parisian apartment reverberates around the globe. In an instant, a suspected terrorist is dead and half a million dollars has vanished. Within days, the CIA is certain it has found a connection between the dead man and a planned terrorist attack on U.S. soil. Determined to avert another 9/11, they have assembled an elite counterterrorist task force, code name: Blood Money. Its mission: to follow the money trail. Its secret weapon: forensic accountant Adam Chapel. A man who trusts numbers more than people, Chapel has his own reasons for wanting to get the job done-- four of his colleagues were killed in the Paris blast. Now Chapel is thrust back into the line of fire when he teams up with British intelligence agent Sarah Churchill. The two are assigned to hunt down a shadowy mastermind who is moving vast sums of money from country to country, from bank to bank, leaving no tracks--as he prepares for an Armaggedon of his own devising. As Chapel follows a disappearing money trail from Paris to Munich to the deserts of Saudi Arabia, Sarah uses her elite training to stalk the “shadow” and his elusive network. Meanwhile, their quarry is auditing their every move, laying a twisting trail of false clues and shocking surprises. With the clock ticking down, soon Chapel and Sarah have only days, hours, minutes to avert disaster as a master terrorist plots to unleash the first strike in a brilliantly orchestrated conspiracy--with an almost unimaginable goal. Hurtling us from the winding alleys of Pakistan to the elite banking houses of Europe, The Devil’s Banker creates an adrenaline-fueled world where following the money has never been more dangerous, and evil has never been harder to unmask.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1546503932i/9416.jpg', '9780440241420', '[''Chick Lit'', ''Fiction'', ''Romance'', ''Contemporary'', ''Humor'', ''Adult'', ''Contemporary Romance'']', 'en', '2000-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Les Misérables', 79, 0, 1260, 0, 0, '', '', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1411852091i/24280.jpg', '9780451525260', '[''Fiction'', ''Historical Fiction'', ''Literature'', ''France'', ''Historical'', ''Novels'', ''French Literature'']', 'en', '1862-03-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Storm of Swords', 26, 0, 1009, 0, 0, 'Random House Worlds', 'THE BOOK BEHIND THE THIRD SEASON OF GAME OF THRONES, AN ORIGINAL SERIES NOW ON HBO. Here is the third book in the landmark series that has redefined imaginative fiction and become a modern masterpiece. A STORM OF SWORDS Of the five contenders for power, one is dead, another in disfavor, and still the wars rage. Joffrey sits on the Iron Throne, the uneasy ruler of the Seven Kingdoms. His most bitter rival, Lord Stannis, stands defeated and disgraced, victim of the sorceress who holds him in her thrall. Young Robb still rules the North from the fortress of Riverrun. Meanwhile, making her way across a blood-drenched continent is the exiled queen, Daenerys, mistress of the only three dragons left in the world. As opposing forces maneuver for the final showdown, an army of barbaric wildlings arrives from the outermost limits of civilization, accompanied by a supernatural army of the living dead. As the future of the land hangs in the balance, no one will rest until the Seven Kingdoms have exploded in a veritable storm of swords. . . . A GAME OF THRONES • A CLASH OF KINGS • A STORM OF SWORDS • A FEAST FOR CROWS • A DANCE WITH DRAGONS', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1567840212i/10572.jpg', '9780553381700', '[''Fantasy'', ''Fiction'', ''Epic Fantasy'', ''High Fantasy'', ''Science Fiction Fantasy'', ''Audiobook'', ''Adult'']', 'en', '1998-11-16');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Paradiso Diaspora', 80, 0, 116, 0, 0, 'Penguin Group', 'As the anagram of its title suggests, the poems, prose, and memoir in Yau''s new collection focus on an inescapable duality. High school & older.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1631287935i/10441.jpg', '9780143037150', '[''Fiction'', ''Book Club'', ''Contemporary'', ''Historical Fiction'', ''Adult Fiction'', ''Adult'', ''Drama'']', 'en', '2005-06-23');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Girl You Left Behind', 81, 0, 369, 0, 0, 'Pamela Dorman Books', 'Unwillingly rendered an object of obsession by the Kommandant occupying her small French town in World War I, Sophie risks everything to reunite with her husband a century before a widowed Liv tests her resolve to claim ownership of Sophie''s portrait. By the best-selling author of Me Before You.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1357108762i/15507958.jpg', '9780670026610', '[''Fiction'', ''Contemporary'', ''Book Club'', ''Chick Lit'', ''Adult'', ''Audiobook'', ''Adult Fiction'']', 'en', '2012-12-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Catch-22', 82, 0, 464, 0, 0, 'Simon & Schuster', 'Catch-22 is like no other novel. It is one of the funniest books ever written, a keystone work in American literature, and even added a new term to the dictionary. At the heart of Catch-22 resides the incomparable, malingering bombardier, Yossarian, a hero endlessly inventive in his schemes to save his skin from the horrible chances of war. His efforts are perfectly understandable because as he furiously scrambles, thousands of people he hasn''t even met are trying to kill him. His problem is Colonel Cathcart, who keeps raising the number of missions the men must fly to complete their service. Yet if Yossarian makes any attempts to excuse himself from the perilous missions that he is committed to flying, he is trapped by the Great Loyalty Oath Crusade, the hilariously sinister bureaucratic rule from which the book takes its title: a man is considered insane if he willingly continues to fly dangerous combat missions, but if he makes the necessary formal request to be relieved of such missions, the very act of making the request proves that he is sane and therefore ineligible to be relieved. Catch-22 is a microcosm of the twentieth-century world as it might look to some one dangerously sane -- a masterpiece of our time.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1463157317i/168668.jpg', '9780684833390', '[''Classics'', ''Fiction'', ''Historical Fiction'', ''War'', ''Humor'', ''Literature'', ''Novels'']', 'en', '1961-11-10');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Tuesdays with Morrie', 83, 0, 210, 0, 0, 'Time Warner Books UK', '"Maybe it was a grandparent, or a teacher, or a colleague. Someone older, patient and wise, who understood you when you were young and searching, helped you see the world as a more profound place, gave you sound advice to help you make your way through it. For Mitch Albom, that person was Morrie Schwartz, his college professor from nearly twenty years ago. Maybe, like Mitch, you lost track of this mentor as you made your way, and the insights faded, and the world seemed colder. Wouldn''t you like to see that person again, ask the bigger questions that still haunt you, receive wisdom for your busy life today the way you once did when you were younger? Mitch Albom had that second chance. He rediscovered Morrie in the last months of the older man''s life. Knowing he was dying, Morrie visited with Mitch in his study every Tuesday, just as they used to back in college. Their rekindled relationship turned into one final "class": lessons in how to live."--Back cover.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1423763749i/6900.jpg', '9780751529810', '[''Nonfiction'', ''Memoir'', ''Biography'', ''Classics'', ''Philosophy'', ''Self Help'', ''Inspirational'']', 'en', '1997-08-18');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('How to Be Alone', 84, 0, 318, 0, 0, 'Picador', 'From the National Book Award-winning author of The Corrections, a collection of essays that reveal him to be one of our sharpest, toughest, and most entertaining social critics While the essays in this collection range in subject matter from the sex-advice industry to the way a supermax prison works, each one wrestles with the essential themes of Franzen''s writing: the erosion of civil life and private dignity; and the hidden persistence of loneliness in postmodern, imperial America. Reprinted here for the first time is Franzen''s controversial l996 investigation of the fate of the American novel in what became known as "the Harper''s essay," as well as his award-winning narrative of his father''s struggle with Alzheimer''s disease, and a rueful account of his brief tenure as an Oprah Winfrey author.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1437029776i/2187.jpg', '9780312422160', '[''Fiction'', ''Historical Fiction'', ''Book Club'', ''Contemporary'', ''LGBT'', ''Classics'', ''Literary Fiction'']', 'en', '2002-09-04');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Adventures of Tom Sawyer', 85, 0, 231, 0, 0, 'Penguin Classics', 'The classic adventure story of boyhood escapades on the shores of the Mississippi chronicles the exploits of the mischievous Tom Sawyer and his friends. Reissue.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1404811979i/24583.jpg', '9780143039560', '[''Fiction'', ''Classics'', ''Adventure'', ''Historical Fiction'', ''Young Adult'', ''Literature'', ''Childrens'']', 'en', '1876-06-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Joy Luck Club', 86, 0, 353, 0, 0, 'Penguin', '“The Joy Luck Club is one of my favorite books. From the moment I first started reading it, I knew it was going to be incredible. For me, it was one of those once-in-a-lifetime reading experiences that you cherish forever. It inspired me as a writer and still remains hugely inspirational.” —Kevin Kwan, author of Crazy Rich Asians Amy Tan’s beloved, New York Times bestselling tale of mothers and daughters, now the focus of a new documentary Amy Tan: Unintended Memoir on Netflix Four mothers, four daughters, four families whose histories shift with the four winds depending on who''s "saying" the stories. In 1949 four Chinese women, recent immigrants to San Francisco, begin meeting to eat dim sum, play mahjong, and talk. United in shared unspeakable loss and hope, they call themselves the Joy Luck Club. Rather than sink into tragedy, they choose to gather to raise their spirits and money. "To despair was to wish back for something already lost. Or to prolong what was already unbearable." Forty years later the stories and history continue. With wit and sensitivity, Amy Tan examines the sometimes painful, often tender, and always deep connection between mothers and daughters. As each woman reveals her secrets, trying to unravel the truth about her life, the strings become more tangled, more entwined. Mothers boast or despair over daughters, and daughters roll their eyes even as they feel the inextricable tightening of their matriarchal ties. Tan is an astute storyteller, enticing readers to immerse themselves into these lives of complexity and mystery.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1304978653i/7763.jpg', '9780143038090', '[''Fiction'', ''Historical Fiction'', ''Classics'', ''China'', ''Contemporary'', ''Book Club'', ''Historical'']', 'en', '1989-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Second Summer of the Sisterhood', 87, 0, 373, 0, 0, 'Delacorte Books for Young Readers', 'When teenager Carmen and her three friends discover that a pair of worn jeans from a thrift shop provides an uncanny fit for all four of them, they decide to form a sisterhood, with the pants as the symbolic bond of friendship among them, and embark on asweet-sixteen summer.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1461611233i/452306.jpg', '9780385729340', '[''Young Adult'', ''Fiction'', ''Chick Lit'', ''Contemporary'', ''Romance'', ''Teen'', ''Realistic Fiction'']', 'en', '2001-09-11');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Brain Lock', 88, 0, 264, 0, 0, 'Harper Perennial', 'An estimated 5 million Americans suffer from obsessive-compulsive disorder (OCD) and live diminished lives in which they are compelled to obsess about something or to repeat a similar task over and over. Traditionally, OCD has been treated with Prozac or similar drugs. The problem with medication, aside from its cost, is that 30 percent of people treated don''t respond to it, and when the pills stop, the symptoms invariably return. In Brain Lock, Jeffrey M. Schwartz presents a simple four-step method for overcoming OCD that is so effective, it''s now used in academic treatment centers throughout the world. Proven by brain-imaging tests to actually alter the brain''s chemistry, this method doesn''t rely on psychopharmaceuticals. Instead, patients use cognitive self-therapy and behavior modification to develop new patterns of response to their obsessions. In essence, they use the mind to fix the brain. Using the real-life stories of actual patients, Brain Lock explains this revolutionary method and provides readers with the inspiration and tools to free themselves from their psychic prisons and regain control of their lives.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1437733293i/37442.jpg', '9780060987110', '[''Fantasy'', ''Fiction'', ''Adult'', ''Book Club'', ''Retellings'', ''Audiobook'', ''Witches'']', 'en', '1995-09-29');
