INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Shogun', 90, 0, 1125, 0, 0, 'Hodder & Stoughton', 'This is James Clavell''s tour-de-force; an epic saga of one Pilot-Major John Blackthorne, and his integration into the struggles and strife of feudal Japan. Both entertaining and incisive, Shugun is a stunningly dramatic re-creation of a very different world. Starting with his shipwreck on this most alien of shores, the novel charts Blackthorne''s rise from the status of reviled foreigner up to the hights of trusted advisor and eventually, Samurai. All as civil war looms over the fragile country.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1434908555i/234225.jpg', '9780340839940', '[''Science Fiction'', ''Fiction'', ''Fantasy'', ''Audiobook'', ''Science Fiction Fantasy'', ''Classics'', ''Novels'']', 'en', '1965-06-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Steve Jobs', 91, 0, 656, 0, 0, 'Simon and Schuster', 'Based on more than 40 interviews with Jobs conducted over two years--as well as interviews with more than 100 family members, friends, adversaries, competitors, and colleagues--Isaacson has written a riveting story of the roller-coaster life and searingly intense personality of a creative entrepreneur whose passion for perfection and ferocious drive revolutionized six industries: personal computers, animated movies, music, phones, tablet computing, and digital publishing.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1511288482i/11084145.jpg', '9781451648540', '[''Biography'', ''Nonfiction'', ''Business'', ''Technology'', ''Audiobook'', ''History'', ''Biography Memoir'']', 'en', '2011-10-01');
