INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('If I Stay', 107, 0, 0, 0, 0, 'National Geographic Books', 'In a single moment, everything changes. Seventeen year-old Mia has no memory of the accident; she can only recall riding along the snow-wet Oregon road with her family. Then, in a blink, she finds herself watching as her own damaged body is taken from the wreck... A sophisticated, layered, and heartachingly beautiful story about the power of family and friends, the choices we all make—and the ultimate choice Mia commands.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1630573973i/4374400.jpg', '9780525421030', '[''Young Adult'', ''Romance'', ''Contemporary'', ''Fiction'', ''Realistic Fiction'', ''Teen'', ''Music'']', 'en', '2009-04-02');
