INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Green Eggs and Ham', 111, 0, 68, 0, 0, 'Random House Books for Young Readers', 'Join in the fun with Sam-I-Am in this iconic Dr. Seuss classic about the joy of trying new things. And don’t miss the Netflix series adaptation! I do not like green eggs and ham. I do not like them, Sam-I-am. With unforgettable characters and signature rhymes, Dr. Seuss’s beloved favorite has cemented its place as a children’s classic. Kids will love the terrific tongue twisters as the list of places to enjoy green eggs and ham gets longer and longer...and they might even find themselves craving something new! Beginner Books are fun, funny, and easy to read! Launched by Dr. Seuss in 1957 with the publication of The Cat in the Hat, this beloved early reader series motivates children to read on their own by using simple words with illustrations that give clues to their meaning. Featuring a combination of kid appeal, supportive vocabulary, and bright, cheerful art, Beginner Books will encourage a love of reading in children ages 3–7.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1673212751i/23772.jpg', '9780394800160', '[''Childrens'', ''Picture Books'', ''Classics'', ''Fiction'', ''Poetry'', ''Fantasy'', ''Humor'']', 'en', '1960-08-12');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Charlie and the Chocolate Factory', 112, 0, 186, 0, 0, 'Puffin', 'Each of five children lucky enough to discover an entry ticket into Mr. Willy Wonka''s mysterious chocolate factory takes advantage of the situation in his own way.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1309211401i/6310.jpg', '9780142403880', '[''Fiction'', ''Fantasy'', ''Childrens'', ''Young Adult'', ''Middle Grade'', ''Classics'', ''Humor'']', 'en', '1964-01-17');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Great Expectations', 58, 0, 548, 0, 0, 'Oxford University Press, USA', 'I called myself Pip, and came to be called Pip.'' Great Expectations charts the progress of Pip from childhood through often painful experiences to adulthood, as he moves from the Kent marshes to busy, commercial London, encoutering a variety of extraordinary characters ranging from Magwitch, the excaped convict, to Miss Havisham, locked up withher unhappy past and living with her ward, the arrogant, beautiful Estella. In this compelling story, Dickens shows the dangers of being driven by desire for wealth and social status. Pip must establish his own sense of self against the plans which others seem to have for him, and thus discover afirm set of values and priorities. Whether such values will allow one to prosper in the complex world of early Victorian England is, however, the major question posed by Great Expectations, one of Dicken''s most fascinating, and disturbing, novels. This edition use the text of the Clarendon edition, with a new Introduction and Explanatory Notes. The Appendices give the original, discarded ending, Dicken''s brief working notes, and the serial instalments and chapter divisions in different editions.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1631687432i/2623.jpg', '9780192833590', '[''Classics'', ''Fiction'', ''Literature'', ''Historical Fiction'', ''School'', ''Novels'', ''Classic Literature'']', 'en', '1861-01-01');
