INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Shack', 118, 0, 256, 0, 0, 'Windblown Media', 'After his daughter''s murder, a grieving father confronts God with desperate questions -- and finds unexpected answers -- in this riveting and deeply moving #1 NYT bestseller. When Mackenzie Allen Phillips''s youngest daughter Missy is abducted during a family vacation, he remains hopeful that she''ll return home. But then, he discovers evidence that she may have been brutally murdered in an abandoned shack deep in the Oregon wilderness. Four years later, in this midst of his great sadness, Mack receives a suspicious note that''s supposedly from God, inviting him back to that shack for a weekend. Against his better judgment, he arrives on a wintry afternoon and walks back into his darkest nightmare. What he finds there will change his life forever.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1344270232i/1812457.jpg', '9780964729230', '[''Fiction'', ''Christian'', ''Christian Fiction'', ''Book Club'', ''Spirituality'', ''Faith'', ''Religion'']', 'en', '2007-05-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Misery', 51, 0, 358, 0, 0, 'Berkley', 'Using a needle, an ax, or something worse, Annie encourages Paul to write his best novel -- just for her.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1309376909i/18342.jpg', '9780451169520', '[''Horror'', ''Fiction'', ''Thriller'', ''Fantasy'', ''Classics'', ''Audiobook'', ''Mystery'']', 'en', '1986-09-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Bell Jar', 119, 0, 292, 0, 0, 'Harper Perennial Modern Classics', 'A Special Paperback Edition to Commemorate the Fiftieth Anniversary of the Publication of Sylvia Plath''s Remarkable Novel Sylvia Plath''s shocking, realistic, and intensely emotional novel about a woman falling into the grip of insanity Esther Greenwood is brilliant, beautiful, enormously talented, and successful, but slowly going under—maybe for the last time. In her acclaimed and enduring masterwork, Sylvia Plath brilliantly draws the reader into Esther''s breakdown with such intensity that her insanity becomes palpably real, even rational—as accessible an experience as going to the movies. A deep penetration into the darkest and most harrowing corners of the human psyche, The Bell Jar is an extraordinary accomplishment and a haunting American classic.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1554582218i/6514.jpg', '9780061148510', '[''Fiction'', ''Feminism'', ''Mental Health'', ''Psychology'', ''Literary Fiction'', ''Literature'', ''Book Club'']', 'en', '1963-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Angela''s Ashes', 120, 0, 468, 0, 0, 'HarperCollins UK', 'Stunning re-issue of the phenomenal worldwide bestseller. Angela''s Ashes is a story of extreme hardship and suffering, from Brooklyn tenements to Limerick slums, of courage and survival against seemingly overwhelming odds. It is imbued on every page with McCourt''s distinctive humour and compassion.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1348317139i/252577.jpg', '9780007205230', '[''Nonfiction'', ''Memoir'', ''Biography'', ''Classics'', ''Ireland'', ''Autobiography'', ''Biography Memoir'']', 'en', '1996-09-05');
