INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Isaac Asimov''s Halloween', 135, 0, 256, 0, 0, 'Ace', 'From the award-winning pages of Isaac Asimov''s Science Fiction Magazine comes this new anthology of stories for All Hallow''s Eve, featuring tales from noted authors Nancy Kress, Ian R. MacLeod, Esther M. Friesner, Lawrence Watt-Evans, and others.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1468560853i/301082.jpg', '9780441008540', '[''Fantasy'', ''Vampires'', ''Paranormal'', ''Romance'', ''Urban Fantasy'', ''Fiction'', ''Mystery'']', 'en', '2001-05-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('And Then There Were None', 137, 0, 292, 0, 0, 'Macmillan', 'And Then There Were None is the signature novel of Agatha Christie, the most popular work of the world''s bestselling novelist. It is a masterpiece of mystery and suspense that has been a fixture in popular literature since it was originally published in 1939. First there were ten-a curious assortment of strangers summoned to a private island off the coast of Devon. Their host, an eccentric millionaire unknown to any of them, is nowhere to be found. The ten guests have precious little in common except that each has a deadly secret buried deep in their own past. And, unknown to them, each has been marked for murder. Alone on the island and trapped by foul weather, one by one the guests begin to fall prey to the hidden murderer among them. With themselves as the only suspects, only the dead are above suspicion.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1638425885i/16299.jpg', '9780312330870', '[''Mystery'', ''Fiction'', ''Thriller'', ''Crime'', ''Mystery Thriller'', ''Book Club'', ''Classics'']', 'en', '1939-11-06');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Lost Symbol', 8, 0, 0, 0, 0, 'National Geographic Books', '#1 WORLDWIDE BESTSELLER • An intelligent, lightning-paced thriller set within the hidden chambers, tunnels, and temples of Washington, D.C., with surprises at every turn. • Don’t miss the Peacock original series Dan Brown’s The Lost Symbol! Famed Harvard symbologist Robert Langdon answers an unexpected summons to appear at the U.S. Capitol Building. His plans are interrupted when a disturbing object—artfully encoded with five symbols—is discovered in the building. Langdon recognizes in the find an ancient invitation into a lost world of esoteric, potentially dangerous wisdom. When his mentor Peter Solomon—a long-standing Mason and beloved philanthropist—is kidnapped, Langdon realizes that the only way to save Solomon is to accept the mystical invitation and plunge headlong into a clandestine world of Masonic secrets, hidden history, and one inconceivable truth ... all under the watchful eye of Dan Brown''s most terrifying villain to date.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1534070883i/6411961.jpg', '9780385504220', '[''Fiction'', ''Mystery'', ''Thriller'', ''Mystery Thriller'', ''Suspense'', ''Adventure'', ''Historical Fiction'']', 'en', '2009-09-15');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Hush, Hush', 138, 0, 432, 0, 0, 'Simon and Schuster', 'Sixteen-year old Nora finds forbidden love with a fallen angel, the New York Times Bestseller, now in paperback!', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1358261334i/6339664.jpg', '9781416989420', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Paranormal'', ''Angels'', ''Paranormal Romance'', ''Fiction'']', 'en', '2009-10-13');
