INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Clockwork Prince', 34, 0, 528, 0, 0, 'Simon and Schuster', 'As Council tries to strip Charlotte of her power, shapechanger Tessa Gray works with the London Shadowhunters.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1460477747i/10025305.jpg', '9781416975880', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Paranormal'', ''Steampunk'', ''Historical Fiction'', ''Urban Fantasy'']', 'en', '2011-12-06');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Yes Please', 230, 0, 352, 0, 0, 'Dey Street Books', 'In a perfect world . . . We''d get to hang out with Amy Poehler, watching dumb movies, listening to music, and swapping tales about our coworkers and difficult childhoods. Because in a perfect world, we''d all be friends with Amy—someone who seems so fun, is full of interesting stories, tells great jokes, and offers plenty of advice and wisdom (the useful kind, not the annoying kind you didn''t ask for, anyway). Unfortunately, between her Golden Globe-winning role on Parks and Recreation, work as a producer and director, place as one of the most beloved SNL alumni and cofounder of the Upright Citizens'' Brigade, involvement with the website Smart Girls at the Party, frequent turns as acting double for Meryl Streep, and her other gig as the mom of two young sons, she''s not available for movie night. Luckily we have the next best thing: Yes Please, Amy''s hilarious and candid book. A collection of stories, thoughts, ideas, lists, and haikus from the mind of one of our most beloved entertainers, Yes Please offers Amy''s thoughts on everything from her "too safe" childhood outside of Boston to her early days in New York City, her ideas about Hollywood and "the biz," the demon that looks back at all of us in the mirror, and her joy at being told she has a "face for wigs." Yes Please is chock-full of words and wisdom to live by.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1402815435i/20910157.jpg', '9780062268340', '[''Memoir'', ''Humor'', ''Audiobook'', ''Comedy'', ''Autobiography'', ''Biography Memoir'', ''Book Club'']', 'en', '2014-10-28');
