INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Three Tales of My Father''s Dragon', 256, 0, 253, 0, 0, 'Random House Books for Young Readers', 'Rediscover the classic fantasy trilogy beginning with Newbery Honor winner My Father''s Dragon in this 50th anniversary edition that includes three timeless tales in one, with original illustrations! A perfect family read aloud that has delighted parents and children for generations--this is the perfect gift to share some magic this Father''s Day! My Father''s Dragon When Elmer Elevator hears about the plight of an overworked and underappreciated baby flying dragon, he stows away on a ship and travels to Wild Island to rescue the dragon. Elmer and the Dragon A stand-alone sequel to My Father''s Dragon, in which Elmer Elevator and the flying baby dragon help the king of the canaries find treasure. The Dragons of Blueland In this third and final installment of the classic My Father''s Dragon trilogy, Boris, the baby dragon, is excited to be heading home to Blueland and looks forward to reuniting with his family.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1705520064i/7784.jpg', '9780679889110', '[''Childrens'', ''Picture Books'', ''Fiction'', ''Classics'', ''Poetry'', ''Fantasy'', ''Environment'']', 'en', '1971-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Poison Diaries', 257, 0, 88, 0, 0, '', 'The Duchess, who is responsible for creating the Poison Garden Alnwick Castle in England, presents a story--told through the journals of a young apprentice to an evil apothecary--of the battle between good and evil which is also educational parable of the curative and lethal properties of plants. Illustrations. Young adult.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388183826i/389627.jpg', '9780810993140', '[''Fiction'', ''Humor'', ''Childrens'', ''Middle Grade'', ''Young Adult'', ''Realistic Fiction'', ''Graphic Novels'']', 'en', '2007-04-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Madame Pamplemousse and Her Incredible Edibles', 258, 0, 145, 0, 0, 'Bloomsbury Publishing USA', 'Madame Pamplemousse is the story of Madeleine, forced to work in her unpleasant uncle''s horrible restaurant, The Squealing Pig. By chance she comes across the most marvellous shop, run by Madame Pamplemousse, which is quiet, discreet, yet full of delicious and otherworldly ''edibles'' - Pterodactyl Bacon, Scorpion Tails in Smoked Garlic Oil, and Great Squid Tentacle in Jasmine-Scented Jelly. A quiet comradeship develops between Madeleine, Madame Pamplemousse, and Madame''s cat, Camembert. And together they create some wonderful culinary magic. Exquisite, beautifully formed prose that has echoes of Angela Carter belies a narrative that is full of pace. A wonderful fairy tale that will appeal to both adults and children.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1495278054i/7896527.jpg', '9781599906960', '[''Young Adult'', ''Romance'', ''Fiction'', ''Romantasy'', ''Magic'', ''Young Adult Fantasy'', ''Audiobook'']', 'en', '2012-08-07');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Shadow Kiss', 251, 0, 0, 0, 0, 'National Geographic Books', 'Is Rose’s fate to kill the person she loves most? It’s springtime at St. Vladimir’s Academy, and Rose Hathaway is this close to graduation. Since Mason’s death, Rose hasn’t been feeling quite right. She has dark flashbacks in the middle of practice, can’t concentrate in class, and has terrifying dreams about Lissa. But Rose has an even bigger secret . . . .She’s in love with Dimitri. And this time, it’s way more than a crush. Then Strigoi target the academy in the deadliest attack in Moroi history, and Dimitri is taken. Rose must protect Lissa at all costs, but keeping her best friend safe could mean losing Dimitri forever…', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1361099049i/2802316.jpg', '9781595141970', '[''Young Adult'', ''Fantasy'', ''Vampires'', ''Paranormal'', ''Romance'', ''Urban Fantasy'', ''Paranormal Romance'']', 'en', '2008-11-13');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Three Gospels', 259, 0, 296, 0, 0, 'Scribner Book Company', 'In his first work of nonfiction since A Whole New Life, one of America''s most respected men of letters gives readers fresh translations of the New Testament gospels of Mark and John--as well as his own modern gospel, which is grounded in a comprehensive knowledge of ancient sources.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1492591524i/46170.jpg', '9780684803360', '[''Classics'', ''Historical Fiction'', ''Literature'', ''War'', ''Novels'', ''American'', ''Spain'']', 'en', '1940-10-01');
