INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Fast Food Nation', 262, 0, 426, 0, 0, 'Harper Perennial', 'Fast food has hastened the malling of our landscape, widened the chasm between rich and poor, fueled an epidemic of obesity, and propelled American cultural imperialism abroad. That''s a lengthy list of charges, but Eric Schlosser makes them stick with an artful mix of first-rate reportage, wry wit, and careful reasoning. Schlosser''s myth-shattering survey stretches from California''s subdivisions, where the business was born, to the industrial corridor along the New Jersey Turnpike, where many of fast food''s flavors are concocted. Along the way, he unearths a trove of fascinating, unsettling truths -- from the unholy alliance between fast food and Hollywood to the seismic changes the industry has wrought in food production, popular culture, and even real estate.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1554229727i/1097.jpg', '9780060838580', '[''Food'', ''Sociology'', ''Politics'', ''History'', ''Science'', ''School'', ''Business'']', 'en', '2001-01-17');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Locust and the Bird', 263, 0, 328, 0, 0, 'Bloomsbury Publishing', 'Biographies and Autobiographies.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1630876355i/3950967.jpg', '9780747599880', '[''Fantasy'', ''Fiction'', ''Young Adult'', ''Short Stories'', ''Childrens'', ''Magic'', ''Middle Grade'']', 'en', '2008-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Tell-Tale Heart', 264, 0, 450, 0, 0, 'Bantam Classics', 'Edgar Allan Poe remains the unsurpassed master of works of mystery and madness in this outstanding collection of Poe''s prose and poetry are sixteen of his finest tales, including "The Tell-Tale Heart", "The Murders in the Rue Morgue", "The Fall of the House of Usher," "The Pit and the Pendulum," "William Wilson," "The Black Cat," "The Cask of Amontillado," and "Eleonora". Here too is a major selection of what Poe characterized as the passion of his life, his poems - "The Raven," "Annabel Lee," Ulalume," "Lenore," "The Bells," and more, plus his glorious prose poem "Silence - A Fable" and only full-length novel, The Narrative of Arthur Gordon Pym.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1697731059i/391729.jpg', '9780553212280', '[''Classics'', ''Horror'', ''Fiction'', ''Short Stories'', ''Poetry'', ''Gothic'', ''Mystery'']', 'en', '1843-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('When Things Fall Apart', 265, 0, 203, 0, 0, 'Shambhala Publications', 'Describes a traditional Buddhist approach to suffering and how embracing the painful situation and using communication, negative habits, and challenging experiences leads to emotional growth and happiness.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1630683326i/10534.jpg', '9781590302260', '[''Nonfiction'', ''Philosophy'', ''History'', ''War'', ''Politics'', ''Business'', ''Classics'']', 'en', '2005-01-11');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Vonnegut Encyclopedia', 266, 0, 786, 0, 0, 'Delacorte Press', 'Now expanded and updated, this authorized compendium to Kurt Vonnegut’s novels, stories, essays, and plays is the most comprehensive and definitive edition to date. Over the course of five decades, Kurt Vonnegut created a complex and interconnected web of characters, settings, and concepts. The Vonnegut Encyclopedia is an exhaustive guide to this beloved author’s world, organized in a handy A-to-Z format. The first edition of this book covered Vonnegut’s work through 1991. This new and updated edition encompasses his writing through his death in 2007. Marc Leeds, co-founder and founding president of the Kurt Vonnegut Society and a longtime personal friend of the author’s, has devoted more than twenty-five years of his life to cataloging the Vonnegut cosmos—from the birthplace of Kilgore Trout (Vonnegut’s sci-fi writing alter ego) to the municipal landmarks of Midland City (the midwestern metropolis that is the setting for Vonnegut’s 1973 masterpiece Breakfast of Champions). The Vonnegut Encyclopedia identifies every major and minor Vonnegut character from Celia Aamons to Zog, as well as recurring images and relevant themes from all of Vonnegut’s works, including lesser-known gems like his revisionist libretto for Stravinsky’s opera L’Histoire du soldat and his 1980 children’s book Sun Moon Star. Leeds provides expert notes explaining the significance of many items, but relies primarily on extended quotations from Vonnegut himself. A work of impressive scholarship in an eminently browsable package, this encyclopedia reveals countless connections readers may never have thought of on their own. A rarity among authors of serious fiction, Kurt Vonnegut has always inspired something like obsession in his most dedicated fans. The Vonnegut Encyclopedia is an invaluable resource for readers wishing to revisit his fictional universe—and those about to explore it for the first time. Praise for The Vonnegut Encyclopedia “An essential collection for fans of the singular satirist.”—San Francisco Chronicle “Indispensable.”—Publishers Weekly “If you’re somebody who has read one Kurt Vonnegut book then there’s a chance you’ve read them all. For the devout reader of Vonnegut there’s a voracious sense of completism. And, Marc Leeds and his new [The Vonnegut] Encyclopedia are here to guide you through it all. Just don’t blame him if you become unstuck in time while you’re reading.”—Inverse “Vonnegut enthusiasts will be delighted with Leeds’s exhaustive, almost obsessive, treatment of the characters, places, events, and tantalizingly mysterious references for which Vonnegut’s five-decade writing career is celebrated. . . . A wonderful and beautifully designed reference source.”—Booklist (starred review) “Leeds’s scholarship and genuine love for his subject matter render this encyclopedia a treasure trove for Vonnegut readers.”—The Nameless Zine', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1329612158i/11367726.jpg', '9780385344230', '[''Fiction'', ''Book Club'', ''Mystery'', ''Thriller'', ''Crime'', ''Mystery Thriller'', ''Audiobook'']', 'en', '2012-01-31');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Breakfast of Champions', 197, 0, 324, 0, 0, 'Dial Press Trade Paperback', '“Marvelous . . . [Vonnegut] wheels out all the complaints about America and makes them seem fresh, funny, outrageous, hateful and lovable.”—The New York Times In Breakfast of Champions, one of Kurt Vonnegut’s most beloved characters, the aging writer Kilgore Trout, finds to his horror that a Midwest car dealer is taking his fiction as truth. What follows is murderously funny satire, as Vonnegut looks at war, sex, racism, success, politics, and pollution in America and reminds us how to see the truth. “Free-wheeling, wild and great . . . uniquely Vonnegut.”—Publishers Weekly', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327934446i/4980.jpg', '9780385334200', '[''Fiction'', ''Classics'', ''Science Fiction'', ''Humor'', ''Literature'', ''Novels'', ''American'']', 'en', '1973-07-12');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The New Drawing on the Right Side of the Brain', 267, 0, 412, 0, 0, 'Tarcher', 'Whether you feel you have little talent and you doubt you could ever learn, or you enjoy drawing, but have not been able to get much beyond a childlike level, this book aims to give you the skill you have always wanted.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1348133732i/627206.jpg', '9780874774240', '[''Art'', ''Nonfiction'', ''Drawing'', ''Reference'', ''Psychology'', ''Design'', ''Self Help'']', 'en', '1979-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Bel Canto', 268, 0, 352, 0, 0, 'Harper Perennial', 'Somewhere in South America, at the home of the country''s vice president, a lavish birthday party is being held in honor of the powerful businessman Mr. Hosokawa. Roxanne Coss, opera''s most revered soprano, has mesmerized the international guests with her singing. It is a perfect evening -- until a band of gunwielding terrorists takes the entire party hostage. But what begins as a panicked, life-threatening scenario slowly evolves into something quite different, a moment of great beauty, as terrorists and hostages forge unexpected bonds and people from different continents become compatriots, intimate friends, and lovers.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1352997328i/5826.jpg', '9780060838720', '[''Fiction'', ''Book Club'', ''Contemporary'', ''Literary Fiction'', ''Historical Fiction'', ''Audiobook'', ''Music'']', 'en', '2001-05-22');
