INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Color of Magic', 189, 0, 0, 0, 0, 'Harper Perennial', 'Terry Pratchett''s profoundly irreverent, bestselling novels have garnered him a revered position in the halls of parody next to the likes of Mark Twain, Kurt Vonnegut, Douglas Adams, and Carl Hiaasen. The Color of MagicM is Terry Pratchett''s maiden voyage through the now-legendary land of Discworld. This is where it all begins -- with the tourist Twoflower and his wizard guide, Rincewind.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1407111017i/34497.jpg', '9780060855920', '[''Fantasy'', ''Fiction'', ''Humor'', ''Comedy'', ''Science Fiction'', ''Magic'', ''Science Fiction Fantasy'']', 'en', '1983-12-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Rights of the People', 281, 0, 498, 0, 0, 'Vintage', 'An impassioned, incisive look at the violations of civil liberties in the United States that have accelerated over the past decade—and their direct impact on our lives. How have our rights to privacy and justice been undermined? What exactly have we lost? Pulitzer Prize–winner David K. Shipler searches for the answers to these questions by traveling the midnight streets of dangerous neighborhoods with police, listening to traumatized victims of secret surveillance, and digging into dubious terrorism prosecutions. The law comes to life in these pages, where the compelling stories of individual men and women illuminate the broad array of government’s powers to intrude into personal lives. Examining the historical expansion and contraction of fundamental liberties in America, this is the account of what has been taken—and of how much we stand to regain by protesting the departures from the Bill of Rights. And, in Shipler’s hands, each person’s experience serves as a powerful incitement for a retrieval of these precious rights.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1429638085i/4929.jpg', '9781400079280', '[''Fiction'', ''Magical Realism'', ''Fantasy'', ''Japanese Literature'', ''Novels'', ''Literature'', ''Classics'']', 'en', '2002-09-01');
