INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Dark Lover', 282, 0, 393, 0, 0, 'Penguin', 'When one of his most trusted fighters is killed, orphaning a half-breed daughter, Wrath, the only purebred vampire left on Earth, finds Beth Randall and ushers her into the world of the undead where she must fulfill her destiny by his side. Original.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1429676285i/42899.jpg', '9780451216950', '[''Paranormal'', ''Vampires'', ''Paranormal Romance'', ''Fantasy'', ''Urban Fantasy'', ''Adult'', ''Fiction'']', 'en', '2005-02-19');
