INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Untamed', 291, 0, 351, 0, 0, 'Macmillan', 'Zoey''s adventures at vampyre finishing school take a wild and dangerous turn as in this spellbinding fourth House of Night novel.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1438037020i/2866718.jpg', '9780312379830', '[''Young Adult'', ''Vampires'', ''Fantasy'', ''Paranormal'', ''Romance'', ''Fiction'', ''Supernatural'']', 'en', '2008-09-23');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Irish Gold', 321, 0, 512, 0, 0, 'Forge Books', 'Bestselling novelist Andrew M. Greeley outdoes his previous triumphs with Irish Gold, a contemporary, fresh and exciting novel of suspense and love. Nuala Anne McGrail, a student at Dublin''s Trinity College, is beautiful the way a Celtic goddess is beautiful - not that Dermot Michael Coyne of Chicago has ever seen one of those in his twenty-five years - unless you count his grandmother Nell, who left Ireland during the Troubles with her husband Liam O''Riada, and who would never tell why they left. Somebody else remembers, though - or why is Dermot set upon by thugs?', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1295660894i/7967.jpg', '9780812550760', '[''Science Fiction'', ''Fiction'', ''Fantasy'', ''Science Fiction Fantasy'', ''Young Adult'', ''Audiobook'', ''Space'']', 'en', '1986-03-01');
