INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Men of Albermarle', 336, 0, 512, 0, 0, 'Bantam Books', 'Lady Mary had hoped that forsaking London society would ease the pain of her terrible secret but the Carolina wilderness of 1710 provides little comfort', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1320418824i/561909.jpg', '9780553256700', '[''Nonfiction'', ''Biography'', ''History'', ''Christian'', ''Memoir'', ''Classics'', ''Holocaust'']', 'en', '1971-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Karamazov Brothers', 337, 0, 1058, 0, 0, 'Oxford Paperbacks', 'Dostoevsky''s last and greatest novel, The Karamazov Brothers (1880),is both a brilliantly told crime story and a passionate philosophical debate. the dissolute landowner Fyodor Pavlovich Karamazov is murdered; his sons - the atheist intellectual Ivan, the hot-blooded Dmitry, and the saintly novice Alyosha - are all at som elevel involved. Bound up with this intense family drama is Dostoevsky''s exploration of many deeply felt ideas about the existence of God, the question of human freedom, the collective nature of guilt, the disastrous consequences of rationalism. The novel is also richly comic: the Russiam Orthodox Church, the legal system, and even the author''s most cherished causes and beliefs are presented with a note of irreverence, so that orthodoxy and radicalism, sanity and madness, love and hatred, right and wrong are no longer mutually exclusive. Rebecca West considered it "the allegory for the world''s maturity", but with children to the fore. This new translation does full justice to Dostoevsky''s genius, particularly in the use of the spoken word, which ranges over every mode of human expression. the disastrous consequences of rationalism. The novel is also richly comic; the Russian Orthodox Church, the legal system, and even the author''s most cherished characters and causes are presented in an irreverent light, so that there appears no sharp distinction between health and disease or right or wrong.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1164045516i/3590.jpg', '9780192835090', '[''Mystery'', ''Fiction'', ''Short Stories'', ''Crime'', ''Classics'', ''Audiobook'', ''Mystery Thriller'']', 'en', '1892-10-14');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Prince of Tides', 338, 0, 0, 0, 0, 'National Geographic Books', '“A big, sprawling saga of a novel” (San Francisco Chronicle), this epic family drama is a masterwork by the revered author of The Great Santini. Pat Conroy’s classic novel stings with honesty and resounds with drama. Spanning forty years, it’s the story of turbulent Tom Wingo, his gifted and troubled twin sister, Savannah, and their struggle to triumph over the dark and tragic legacy of the extraordinary family into which they were born. Filled with the vanishing beauty of the South Carolina Low Country as well as the dusty glitter of New York City, The Prince of Tides showcases an American original at his very best. Praise for The Prince of Tides “This is a powerful book. . . . [Pat] Conroy is a master of language.”—The Atlanta Journal “A big, sprawling saga of a novel . . . the kind you can hole up with and spend some days with and put down feeling that you’ve emerged from a terrible, wonderful spell.”—San Francisco Chronicle “A literary gem . . . The Prince of Tides is in the best tradition of novel writing. It is an engrossing story of unforgettable characters.”—The Pittsburgh Press “A masterpiece.”—Detroit Free Press “Brilliant.”—Chicago Tribune', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1512744907i/16735.jpg', '9780553381540', '[''Fiction'', ''Classics'', ''Romance'', ''Southern'', ''Contemporary'', ''Literary Fiction'', ''Literature'']', 'en', '1986-01-01');
