INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Cloud Atlas', 342, 0, 509, 0, 0, 'Random House Incorporated', 'Recounts the connected stories of people from the past and the distant future, from a nineteenth-century notary and an investigative journalist in the 1970s to a young man who searches for meaning in a post-apocalyptic world.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1563042852i/49628.jpg', '9780375507250', '[''Fiction'', ''Science Fiction'', ''Fantasy'', ''Historical Fiction'', ''Dystopia'', ''Book Club'', ''Literary Fiction'']', 'en', '2004-03-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Things They Carried', 343, 0, 274, 0, 0, 'Broadway', 'One of the first questions people ask about The Things They Carried is this: Is it a novel, or a collection of short stories? The title page refers to the book simply as "a work of fiction," defying the conscientious reader''s need to categorize this masterpiece. It is both: a collection of interrelated short pieces which ultimately reads with the dramatic force and tension of a novel. Yet each one of the twenty-two short pieces is written with such care, emotional content, and prosaic precision that it could stand on its own. The Things They Carried depicts the men of Alpha Company: Jimmy Cross, Henry Dobbins, Rat Kiley, Mitchell Sanders, Norman Bowker, Kiowa, and of course, the character Tim O''Brien who has survived his tour in Vietnam to become a father and writer at the age of forty-three. They battle the enemy (or maybe more the idea of the enemy), and occasionally each other. In their relationships we see their isolation and loneliness, their rage and fear. They miss their families, their girlfriends and buddies; they miss the lives they left back home. Yet they find sympathy and kindness for strangers (the old man who leads them unscathed through the mine field, the girl who grieves while she dances), and love for each other, because in Vietnam they are the only family they have. We hear the voices of the men and build images upon their dialogue. The way they tell stories about others, we hear them telling stories about themselves. With the creative verve of the greatest fiction and the intimacy of a searing autobiography, The Things They Carried is a testament to the men who risked their lives in America''s most controversial war. It is also a mirror held up to the frailty of humanity. Ultimately The Things They Carried and its myriad protagonists call to order the courage, determination, and luck we all need to survive.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1424663847i/133518.jpg', '9780767902890', '[''Fiction'', ''Historical Fiction'', ''Classics'', ''War'', ''Short Stories'', ''School'', ''Historical'']', 'en', '1990-03-28');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Untitled Novel', 344, 0, 384, 0, 0, '', 'Present-day thriller and ancient-world mystery combine once again in the gripping new novel from the authors of The Rule of Four.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327904853i/114683.jpg', '9781844137880', '[''Art'', ''Nonfiction'', ''Politics'', ''Street Art'', ''Art Design'', ''Photography'', ''Design'']', 'en', '2005-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Pandemonium', 283, 0, 400, 0, 0, 'HarperCollins', 'The old life is dead. But the old Lena is dead too. I buried her. I left her beyond a fence, behind a wall of smoke and ﬂame. In this electrifying follow-up to her acclaimed New York Times bestseller Delirium, Lauren Oliver sets Lena on a dangerous course that hurtles through the unregulated Wilds and into the heart of a growing resistance movement. This riveting, brilliant novel crackles with the fire of fierce defiance, forbidden romance, and the sparks of a revolution about to ignite.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1310371414i/9593911.jpg', '9780061978070', '[''Young Adult'', ''Dystopia'', ''Romance'', ''Fantasy'', ''Science Fiction'', ''Fiction'', ''Audiobook'']', 'en', '2012-02-28');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Nothing Was the Same', 345, 0, 226, 0, 0, 'Vintage', 'A penetrating psychological study of grief viewed from deep inside the experience itself—from the national bestselling author of Unquiet Mind. Kay Redfield Jamison, award-winning professor and writer, changed the way we think about moods and madness. Now Jamison uses her characteristic honesty, wit and eloquence to look back at her relationship with her husband, Richard Wyatt, a renowned scientist who died of cancer.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1557598685i/17231.jpg', '9780307277890', '[''Fiction'', ''Mystery'', ''Thriller'', ''Crime'', ''Horror'', ''Mystery Thriller'', ''Audiobook'']', 'en', '2004-05-05');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Club Dead', 346, 0, 288, 0, 0, 'Orbit Books', 'They know how to make a great cocktail in Club Dead - a twist of lemon, a dash of pepper and a shot of Blood Group O.There''s only one vampire Sookie Stackhouse is involved with (at least voluntarily) and that''s Bill. But recently he''s been kind of distant - like in another state distant. Bill''s sinister and sexy boss Eric has an idea where to find him, which is why Sookie ends up in Jackson, Mississippi, mingling with the under-underworld at Club Dead. It''s a dangerous little haunt where the elitist vamps can go chill and chow on some prime flesh. But when Sookie finally finds Bill - caught in an act of serious betrayal - she''s not sure whether to save him, or sharpen some stakes.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1329501808i/110494.jpg', '9781841493010', '[''Fantasy'', ''Vampires'', ''Paranormal'', ''Urban Fantasy'', ''Romance'', ''Fiction'', ''Mystery'']', 'en', '2002-03-26');
