INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Guardian', 192, 0, 400, 0, 0, 'Grand Central Publishing', 'Julie Barenson''s young husband left her two unexpected gifts before he died - a Great Dane puppy named Singer and the promise that he would always be watching over her. Now four years have passed. Still living in the small town of Swansboro, North Carolina, twenty-nine-year-old Julie is emotionally ready to make a commitment to someone again. But who? Should it be Richard Franklin, the handsome, sophisticated engineer who treats her like a queen? Or Mike Harris, the down-to-earth nice guy who was her husband''s best friend? Choosing one of them should bring her more happiness than she''s had in years. Instead, Julie is soon fighting for her life in a nightmare spawned by a chilling deception and jealousy so poisonous that it has become a murderous desire...', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388195339i/15925.jpg', '9780446696110', '[''Romance'', ''Fiction'', ''Chick Lit'', ''Contemporary'', ''Adult'', ''Contemporary Romance'', ''Adult Fiction'']', 'en', '2003-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Timeline', 373, 0, 514, 0, 0, 'Random House', 'An old man wearing a brown robe is found wandering disoriented in the Arizona desert. He is miles away from any human habitation and has no memory of how he got to be there, or who he is. The only clue to his identity is the plan of a medieval monastery in his pocket. This mystery will catapult a group of young scientists back to the middle Ages into the heart of the Hundred Years'' War. Imagine the risks of such a journey. Imagine the impossible. ''Timeline combines all the ingredients that make Crichton''s books compulsive reading... a brilliantly imagined story'' Iain Pears, Los Angeles Times ''Hollywood''s favourite thriller writer evokes the experience of time travel superbly... a rollicking read'' Observer ''A thrilling race against time'' Express', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1405420745i/7669.jpg', '9780099244720', '[''Science Fiction'', ''Fiction'', ''Time Travel'', ''Thriller'', ''Historical Fiction'', ''Fantasy'', ''Adventure'']', 'en', '1999-11-16');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Candide', 374, 0, 114, 0, 0, 'Courier Corporation', 'One of the world''s great satires since its first publication in 1759. Witty, caustic skewering of romance, science, philosophy, religion, government — nearly all human ideals and institutions.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1345060082i/19380.jpg', '9780486266890', '[''Fiction'', ''Philosophy'', ''France'', ''Classics'', ''Literature'', ''French Literature'', ''Humor'']', 'en', '1759-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Jessie', 51, 0, 411, 0, 0, 'LGF/Le Livre de Poche', 'Par curiosité, par amusement, par amour peut-être, Jessie s''est longtemps prêtée aux bizarreries sexuelles de Gerald, son mari. Puis un jour, elle s''est rebellée. Débattue. Avec une violence qu''elle ne soupçonnait pas. Et à présent la voilà nue, enchaînée à un lit, dans une maison perdue, loin de tout. Un cadavre à ses pieds... Un mauvais rêve ? Non. L''horreur ne fait que commencer. Et jamais le maître de l''épouvante ne nous a encore emmenés aussi loin dans la terrifiante exploration de nos phobies et de nos cauchemars...', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1663053062i/10629.jpg', '9782253147700', '[''Horror'', ''Fiction'', ''Thriller'', ''Fantasy'', ''Paranormal'', ''Supernatural'', ''Mystery'']', 'fr', '1983-04-29');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Are You My Mother?', 375, 0, 64, 0, 0, 'Beginner Books S.', 'ARE YOU MY MOTHER? tells a very simple story for children who have just started to read. Their younger brothers and sisters will also want to follow the baby bird''s quest as he asks everyone and everything he meets, ''Are You My Mother?'' ''I can read it all by myself'' is the Beginner Books motto, and behind it is an understanding of how important it is for children to take pride and pleasure in their early reading. Beginner Books have been designed to appeal directly to children through the use of humour, rhyme, and bright pictures that can be ''read'' even by the non-reading child. Some Beginner Books are simple stories, others are hilarious nonsense: both types have been designed to give children confidence and make them want to go on reading.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1389098758i/197084.jpg', '9780001713220', '[''Childrens'', ''Fiction'', ''Picture Books'', ''Animals'', ''Classics'', ''Family'', ''Kids'']', 'en', '1960-06-12');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Story of Art - 16th Edition', 376, 0, 694, 0, 0, 'Phaidon Press Limited', 'An illustrated introduction to art appreciation with a survey of the major art periods and styles and descriptions of the work and world of the masters.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1389274650i/222078.jpg', '9780714833550', '[''Art'', ''Nonfiction'', ''Art History'', ''Reference'', ''Classics'', ''Design'', ''Architecture'']', 'en', '1950-01-01');
