INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Falcons of Montabard', 377, 0, 480, 0, 0, 'Little, Brown Young Readers', 'Sabin FitzSimon, disgraced son of an earl, has the opportunity to rebuild his career and salvage his reputation when the knight Edmund Strongfist leaves for the Holy Land to offer his sword and services to the King of Jerusalem. He asks Sabin to join him. Sabin is warned to keep away from Strongfist''s daughter Annais, which he does but with a feeling of regret, for he is attracted to Annais''s spirit, her courage and wonderful harp playing. The Holy Land brings its own share of trials for Sabin. The land is in a turmoil of constant warfare and following the capture of the King, Sabin is forced to take command of the fortress of Montabard and marry its recently widowed chateleine.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388211242i/69571.jpg', '9780751532720', '[''Finance'', ''Business'', ''Nonfiction'', ''Self Help'', ''Money'', ''Personal Finance'', ''Personal Development'']', 'en', '1997-04-08');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Silence', 138, 0, 448, 0, 0, 'Simon and Schuster', 'A conclusion to the series that began with Hush, Hush and Crescendo finds Patch and Nora emerging from their efforts to bridge two irreconcilable worlds and to solve riddles in Patch''s dark past in order to fight a last battle together against a formidable adversary who holds the power to shatter everything they have worked for.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1362408152i/10637766.jpg', '9781442426640', '[''Fantasy'', ''Young Adult'', ''Romance'', ''Angels'', ''Paranormal'', ''Paranormal Romance'', ''Fiction'']', 'en', '2011-10-04');
