INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Great and Terrible Beauty', 381, 0, 0, 0, 0, '', 'Following the death of her mother, 16 year-old Gemma Doyle is shipped off from her life in colonial India to a boarding school in England. Lonely and unhappy, she is prone to visions of the future that have an uncomfortable habit of coming true - and who is the mysterious young Indian man who keeps following her?', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1284558475i/3682.jpg', '9780689875340', '[''Fantasy'', ''Young Adult'', ''Historical Fiction'', ''Fiction'', ''Historical'', ''Paranormal'', ''Romance'']', 'en', '2003-12-09');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Everything Is Illuminated', 382, 0, 290, 0, 0, 'Harper Collins', 'With only a yellowing photograph in hand, a young man -- also named Jonathan Safran Foer -- sets out to find the woman who may or may not have saved his grandfather from the Nazis. Accompanied by an old man haunted by memories of the war; an amorous dog named Sammy Davis, Junior, Junior; and the unforgettable Alex, a young Ukrainian translator who speaks in a sublimely butchered English, Jonathan is led on a quixotic journey over a devastated landscape and into an unexpected past.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1479774440i/256566.jpg', '9780060529700', '[''Fiction'', ''Historical Fiction'', ''Contemporary'', ''Novels'', ''Book Club'', ''Magical Realism'', ''Literary Fiction'']', 'en', '2002-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Every Secret Thing', 383, 0, 417, 0, 0, '', 'It is early evening, summer time and hot. Two eleven-year-old girls, Alice and Ronnie, are on their way home from a swimming party when they happen to see a baby''s stroller, with baby girl sleeping inside, left unattended on the top step of a house. Ronnie says to Alice: ''We have to take care of this baby.'' But what exactly does she mean? Four days later the body of little Olivia Barnes is discovered in a hut in Baltimore''s rambling Leakin Park by a young rookie detective, Nancy Porter. What can have happened in those four days to bring about this appalling crime? The girls are arrested and found guilty. Seven years later Ronnie and Alice, now eighteen, are released from their separate prisons, back into their old neighbourhood where the mother of baby Olivia still lives. Another child goes missing, and Nancy Porter and her partner get the case ...', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1335860600i/15769.jpg', '9780752863890', '[''Fiction'', ''Thriller'', ''Mystery'', ''Suspense'', ''Espionage'', ''Action'', ''Adventure'']', 'en', '1986-02-11');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('David Copperfield', 58, 0, 0, 0, 0, 'Penguin', '''The most perfect of all the Dickens novels'' Virginia Woolf David Copperfield is the story of a young man''s adventures on his journey from an unhappy and impoverished childhood to the discovery of his vocation as a successful novelist. Among the gloriously vivid cast of characters he encounters are his tyrannical stepfather, Mr Murdstone; his brilliant, but ultimately unworthy school-friend James Steerforth; his formidable aunt, Betsey Trotwood; the eternally humble, yet treacherous Uriah Heep; frivolous, enchanting Dora Spenlow; and the magnificently impecunious Wilkins Micawber, one of literature''s great comic creations. In David Copperfield - the novel he described as his ''favourite child'' - Dickens drew revealingly on his own experiences to create one of the most exuberant and enduringly popular works, filled with tragedy and comedy in equal measure. This edition uses the text of the first volume publication of 1850, and includes updated suggestions for further reading, original illustrations by ''Phiz'', a revised chronology and expanded notes. In his new introduction, Jeremy Tambling discusses the novel''s autobiographical elements, and its central themes of memory and identity. For more than seventy years, Penguin has been the leading publisher of classic literature in the English-speaking world. With more than 1,700 titles, Penguin Classics represents a global bookshelf of the best works throughout history and across genres and disciplines. Readers trust the series to provide authoritative texts enhanced by introductions and notes by distinguished scholars and contemporary authors, as well as up-to-date translations by award-winning translators.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1461452762i/58696.jpg', '9780140439440', '[''Fiction'', ''Literature'', ''Historical Fiction'', ''Audiobook'', ''19th Century'', ''Novels'', ''Victorian'']', 'en', '1850-11-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Absolutely True Diary of a Part-Time Indian', 384, 0, 254, 0, 0, 'Little, Brown', 'Budding cartoonist Junior leaves his troubled school on the Spokane Indian Reservation to attend an all-white farm town school where the only other Indian is the school mascot.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1327908992i/693208.jpg', '9780316013680', '[''Young Adult'', ''Fiction'', ''Contemporary'', ''Realistic Fiction'', ''Humor'', ''Book Club'', ''Banned Books'']', 'en', '2007-09-12');
