INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Because of Winn-Dixie', 387, 0, 196, 0, 0, 'Candlewick Press (MA)', 'Ten-year-old India Opal Buloni describes her first summer in the town of Naomi, Florida, and all the good things that happen to her because of her big ugly dog Winn-Dixie.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1456871914i/357664.jpg', '9780763616050', '[''Fiction'', ''Middle Grade'', ''Childrens'', ''Realistic Fiction'', ''Young Adult'', ''Classics'', ''Animals'']', 'en', '2000-03-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A Separate Peace', 388, 0, 208, 0, 0, 'Scribner', 'Nominated as one of America’s best-loved novels by PBS’s The Great American Read. An American classic and great bestseller for over thirty years, A Separate Peace is timeless in its description of adolescence during a period when the entire country was losing its innocence to World War II. Set at a boys'' boarding school in New England during the early years of World War II, A Separate Peace is a harrowing and luminous parable of the dark side of adolescence. Gene is a lonely, introverted intellectual. Phineas is a handsome, taunting, daredevil athlete. What happens between the two friends one summer, like the war itself, banishes the innocence of these boys and their world.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1528860889i/5148.jpg', '9780743253970', '[''Classics'', ''Fiction'', ''Historical Fiction'', ''School'', ''Young Adult'', ''Coming Of Age'', ''High School'']', 'en', '1959-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Rainmaker', 89, 0, 577, 0, 0, 'Vintage', '#1 NEW YORK TIMES BESTSELLER • “A taut and terrific page-turner” (Entertainment Weekly) from the master of the courtroom thriller “Great fun to read . . . The complex plotting is Grisham’s major accomplishment.”—Los Angeles Times In development as a USA Network series starring John Slattery It’s summer in Memphis. The sweat is sticking to Rudy Baylor’s shirt and creditors are nipping at his heels. Once he had aspirations of breezing through law school and punching his ticket to the good life. Now he doesn’t have a job or a prayer—except for one: an insurance dispute that leaves a family devastated and opens the door for a lawsuit, if Rudy can find a way to file it. By the time Rudy gets to court, a heavyweight corporate defense team is there to meet him. And suddenly he’s in over his head, plunged into a nightmare of lies and legal maneuverings. A case that started small is exploding into a thunderous million-dollar war of nerves, skill, and outright violence—a fight that could cost one young lawyer his life, or turn him into the biggest rainmaker in the land.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388265477i/5349.jpg', '9780385339600', '[''Fiction'', ''Thriller'', ''Mystery'', ''Legal Thriller'', ''Crime'', ''Suspense'', ''Mystery Thriller'']', 'en', '1995-04-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Don Quixote', 389, 0, 1076, 0, 0, 'Penguin', 'Nominated as one of America’s best-loved novels by PBS’s The Great American Read Don Quixote has become so entranced reading tales of chivalry that he decides to turn knight errant himself. In the company of his faithful squire, Sancho Panza, these exploits blossom in all sorts of wonderful ways. While Quixote''s fancy often leads him astray—he tilts at windmills, imagining them to be giants—Sancho acquires cunning and a certain sagacity. Sane madman and wise fool, they roam the world together-and together they have haunted readers'' imaginations for nearly four hundred years. With its experimental form and literary playfulness, Don Quixote has been generally recognized as the first modern novel. This Penguin Classics edition, with its beautiful new cover design, includes John Rutherford''s masterly translation, which does full justice to the energy and wit of Cervantes''s prose, as well as a brilliant critical introduction by Roberto Gonzalez Echevarriá.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1546112331i/3836.jpg', '9780142437230', '[''Fiction'', ''Classics'', ''Literature'', ''Spanish Literature'', ''Historical Fiction'', ''Adventure'', ''Novels'']', 'en', '1615-01-01');
