INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Dreams From My Father', 421, 0, 461, 0, 0, 'Text Publishing', 'Before Barack Obama became a politician he was, among other things, a writer. Dreams from My Father is his masterpiece: a refreshing, revealing portrait of a young man asking the big questions about identity and belonging. The son of a black African father and a white American mother, Obama recounts an emotional odyssey. He retraces the migration of his mother’s family from Kansas to Hawaii, then to his childhood home in Indonesia. Finally he travels to Kenya, where he confronts the bitter truth of his father’s life and at last reconciles his divided inheritance.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1352340675i/88061.jpg', '9781921351430', '[''Nonfiction'', ''Biography'', ''Memoir'', ''Politics'', ''Autobiography'', ''Audiobook'', ''Biography Memoir'']', 'en', '1995-07-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('This First Thanksgiving Day', 422, 0, 36, 0, 0, 'Harper Collins', 'Countdown to Thanksgiving! This rollicking counting story celebrates the very first Thanksgiving Day with vibrant illustrations and lively verse. Follow the Pilgrim and Wampanoag friends as they prepare for a great feast, and along the way look for the bold turkey on every page -- and for the very sharp of eye, there are all sorts of surprises hidden in the art!', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388889401i/7672.jpg', '9780060541840', '[''Fiction'', ''Science Fiction'', ''Thriller'', ''Adventure'', ''Horror'', ''Mystery'', ''Suspense'']', 'en', '1980-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Alexander and the Terrible, Horrible, No Good, Very Bad Day', 423, 0, 32, 0, 0, 'Simon and Schuster', 'Alexander knew it was going to be a terrible day when he woke up with gum in this hair. And it got worse... His best friend deserted him. There was no dessert in his lunch bag. And, on top of all that, there were lima beans for dinner and kissing on TV! This handsome new edition of Judith Viorst''s classic picture book is sure to charm readers of all ages.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1510014978i/46677.jpg', '9780689711730', '[''Picture Books'', ''Childrens'', ''Fiction'', ''Classics'', ''Realistic Fiction'', ''Humor'', ''Kids'']', 'en', '1972-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('A People''s History of the United States', 424, 0, 768, 0, 0, 'Harper Perennial Modern Classics', 'Known for its lively, clear prose as well as its scholarly research, A People''s History of the United States is the only volume to tell America''s story from the point of view of -- and in the words of -- America''s women, factory workers, African-Americans, Native Americans, working poor, and immigrant laborers. This P.S. edition features an extra 16 pages of insights into the book, including author interviews, recommended reading, and more.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1494279423i/2767.jpg', '9780060838650', '[''History'', ''Nonfiction'', ''Politics'', ''American History'', ''Historical'', ''Audiobook'', ''Social Justice'']', 'en', '1980-01-01');
