INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Permaculture', 430, 0, 324, 0, 0, '', 'In this challenging work, leading permaculture expert David Holmgren questions whether mainstream concepts of sustainability dodge the critical issue of global energy peak, and asks if there are ways to live within nature''s limits whilst providing a secure future for our children and justice for everyone.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1386922393i/16343.jpg', '9780646418440', '[''Mystery'', ''Crime'', ''Audiobook'', ''Mystery Thriller'', ''Classics'', ''Thriller'', ''Murder Mystery'']', 'en', '1920-01-01');
