INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Little House in the Big Woods', 356, 0, 237, 0, 0, 'Harper Collins', 'A year in the life of two young girls growing up on the Wisconsin frontier, as they help their mother with the daily chores, enjoy their father''s stories and singing, and share special occasions when they get together with relatives or neighbors.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1743630755i/8337.jpg', '9780060885370', '[''Classics'', ''Historical Fiction'', ''Childrens'', ''Fiction'', ''Young Adult'', ''Historical'', ''Middle Grade'']', 'en', '1932-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Jonathan Strange & Mr Norrell', 437, 0, 1028, 0, 0, 'Macmillan', 'Magician, Mr. Norrel of Hurtfew Abbey, uses his powers against Napoleon and the French in 1806 and is soon challenged by Jonathan Strange, a novice magician, who becomes obsessed with the Raven King, the twelfth-century founder of English magic and bizarre forms of magic.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1743119988i/14201.jpg', '9780765356150', '[''Fantasy'', ''Fiction'', ''Historical Fiction'', ''Historical'', ''Magic'', ''Audiobook'', ''Science Fiction Fantasy'']', 'en', '2004-09-08');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Journey to the Mountain of God', 438, 0, 0, 0, 0, 'FaithWords', 'Megachurch pastor Ireland details a step-by-step journey to rekindle readers'' hearts for intimacy with God. He offers questionnaires and surveys designed to help readers assess and improve their spiritual conditions.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1388453919i/3463.jpg', '9780446696140', '[''Romance'', ''Fiction'', ''Chick Lit'', ''Contemporary'', ''Adult'', ''Mystery'', ''Contemporary Romance'']', 'en', '2001-09-18');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Heroes of Olympus Book Five: The Blood of Olympus', 109, 0, 528, 0, 0, 'Disney-Hyperion', 'Though the Greek and Roman crewmembers of the Argo II have made progress in their many quests, they still seem no closer to defeating the earth mother, Gaea. Her giants have risen-all of them-and they''re stronger than ever. They must be stopped before the Feast of Spes, when Gaea plans to have two demigods sacrificed in Athens. She needs their blood-the blood of Olympus-in order to wake. The demigods are having more frequent visions of a terrible battle at Camp Half-Blood. The Roman legion from Camp Jupiter, led by Octavian, is almost within striking distance. Though it is tempting to take the Athena Parthenos to Athens to use as a secret weapon, the friends know that the huge statue belongs back on Long Island, where it might be able to stop a war between the two camps. The Athena Parthenos will go west; the Argo II will go east. The gods, still suffering from multiple personality disorder, are useless. How can a handful of young demigods hope to persevere against Gaea''s army of powerful giants? As dangerous as it is to head to Athens, they have no other option. They have sacrificed too much already. And if Gaea wakes, it is game over.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1464201430i/12127810.jpg', '9781423146730', '[''Fantasy'', ''Mythology'', ''Young Adult'', ''Fiction'', ''Middle Grade'', ''Adventure'', ''Greek Mythology'']', 'en', '2013-10-08');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Gone', 439, 0, 0, 0, 0, 'Katherine Tegen Books', 'In the blink of an eye. Everyone disappears. GONE. Except for the young. Teens. Middle schoolers. Toddlers. But not one single adult. No teachers, no cops, no doctors, no parents. Just as suddenly, there are no phones, no internet, no television. No way to get help. And no way to figure out what''s happened. Hunger threatens. Bullies rule. A sinister creature lurks. Animals are mutating. And the teens themselves are changing, developing new talents—unimaginable, dangerous, deadly powers—that grow stronger by the day. It''s a terrifying new world. Sides are being chosen, a fight is shaping up. Townies against rich kids. Bullies against the weak. Powerful against powerless. And time is running out: On your birthday, you disappear just like everyone else...', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1357796831i/2536134.jpg', '9780061448770', '[''Young Adult'', ''Dystopia'', ''Fantasy'', ''Science Fiction'', ''Fiction'', ''Paranormal'', ''Romance'']', 'en', '2008-06-24');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Seventeen Seventy-six', 241, 0, 19, 0, 0, 'Simon and Schuster', 'Chronicles the American Revolution during the year 1776, examining the leadership of George Washington and British commander William Howe and the experiences of American and British troops.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1306787560i/1067.jpg', '9780743226720', '[''History'', ''Nonfiction'', ''American History'', ''Historical'', ''American Revolution'', ''War'', ''Audiobook'']', 'en', '2005-03-20');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('Under the Banner of Heaven', 57, 0, 436, 0, 0, 'Pan Macmillan', 'Krakauer shifts his focus from extremes of physical adventure to extremes of religious belief in this true story of an appalling double murder committed by two Mormon Fundamentalist brothers who insist God commanded them to kill.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1356441391i/10847.jpg', '9780330419120', '[''Nonfiction'', ''True Crime'', ''History'', ''Book Club'', ''Audiobook'', ''Crime'', ''Cults'']', 'en', '2003-07-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('The Audacity of Hope', 421, 0, 386, 0, 0, 'Crown', '#1 NEW YORK TIMES BESTSELLER • Barack Obama’s lucid vision of America’s place in the world and call for a new kind of politics that builds upon our shared understandings as Americans, based on his years in the Senate “In our lowdown, dispiriting era, Obama’s talent for proposing humane, sensible solutions with uplifting, elegant prose does fill one with hope.”—Michael Kazin, The Washington Post In July 2004, four years before his presidency, Barack Obama electrified the Democratic National Convention with an address that spoke to Americans across the political spectrum. One phrase in particular anchored itself in listeners’ minds, a reminder that for all the discord and struggle to be found in our history as a nation, we have always been guided by a dogged optimism in the future, or what Obama called “the audacity of hope.” The Audacity of Hope is Barack Obama’s call for a different brand of politics—a politics for those weary of bitter partisanship and alienated by the “endless clash of armies” we see in congress and on the campaign trail; a politics rooted in the faith, inclusiveness, and nobility of spirit at the heart of “our improbable experiment in democracy.” He explores those forces—from the fear of losing to the perpetual need to raise money to the power of the media—that can stifle even the best-intentioned politician. He also writes, with surprising intimacy and self-deprecating humor, about settling in as a senator, seeking to balance the demands of public service and family life, and his own deepening religious commitment. At the heart of this book is Barack Obama’s vision of how we can move beyond our divisions to tackle concrete problems. He examines the growing economic insecurity of American families, the racial and religious tensions within the body politic, and the transnational threats—from terrorism to pandemic—that gather beyond our shores. And he grapples with the role that faith plays in a democracy—where it is vital and where it must never intrude. Underlying his stories is a vigorous search for connection: the foundation for a radically hopeful political consensus. Only by returning to the principles that gave birth to our Constitution, Obama says, can Americans repair a political process that is broken, and restore to working order a government that has fallen dangerously out of touch with millions of ordinary Americans. Those Americans are out there, he writes—“waiting for Republicans and Democrats to catch up with them.”', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1386925614i/9742.jpg', '9780307237700', '[''Nonfiction'', ''Politics'', ''Biography'', ''Memoir'', ''Audiobook'', ''Autobiography'', ''History'']', 'en', '2006-01-01');
INSERT INTO books (name, author_id, avg_rating, num_pages, rating_count, review_count, publisher, description, image_link, isbn, genre, lang_code, publ_date) VALUES ('On Writing', 51, 0, 321, 0, 0, 'Simon and Schuster', 'The author shares his insights into the craft of writing and offers a humorous perspective on his own experience as a writer.', 'https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1436735207i/10569.jpg', '9780743455960', '[''Nonfiction'', ''Writing'', ''Memoir'', ''Biography'', ''Audiobook'', ''Reference'', ''Autobiography'']', 'en', '2000-06-02');
